    public class SeedClassFeatures
    {
        private static ClassFeatures Create(string name, int level, string description)
        {
            return new ClassFeatures
            {
                Id = Guid.NewGuid(),
                Name = name,
                LevelRequired = level,
                Description = description,
                IsCustom = false
            };
        }

        public static async Task SeedAsync(ApplicationDbContext context)
        {
            //context.ClassFeatures.RemoveRange(await context.ClassFeatures.ToListAsync());
            //await context.SaveChangesAsync();

            if (await context.ClassFeatures.AnyAsync()) return;

            var model = new List<ClassFeatures>
                {
                                    //tutte le classi
                    //per tutte le classi sono al 4-8-12-16-19, escluso il guerriero che sono al 4-6-8-12-14-16-19
                    Create("Aumento dei Punteggi di Caratteristica",4, "La classe può aumentare di 2 un punteggio di caratteristica a sua scelta, oppure può aumentare di 1 due punteggi di caratteristica a sua scelta. Come di consueto, non è consentito aumentare un punteggio di caratteristica a più di 20 utilizzando questo privilegio."),
                    //al 5 livello per Barbaro, Guerriero, Ranger, Paladino
                    Create("Attacco Extra",5, "A partire dal 5° livello, si può attaccare due volte, anziché una, ogni volta che si effettua l'azione Attacco nel proprio turno."),
                    
                //ARTEFICE
                    Create("Invenzione Magica",1, "A partire dal 1° livello, l'artefice può infondere oggetti minuscoli non magici con effetti semplici. Può mantenere attivi un numero di oggetti pari al suo modificatore di Intelligenza. Ogni effetto dura finché non viene annullato o sostituito."),
                    Create("Incantesimi",1, "Al 1° Livello l'artefice ha studiato il funzionamento della magia e come lanciare gli incantesimi incanalandola attraverso gli oggetti."),
                    Create("Infondere negli Oggetti",2, "L'artefice, a partire dal 2°, livello acquisisce la capacità di instillare in oggetti di uso quotidiano alcune infusioni magiche, facendo diventare magici gli oggetti."),
                    Create("Specializzazione da Artefice",3, "Quando l'artefice arriva al 3° livello e poi di nuovo all'5°, al 9° e al 15° livello approfondisce un ramo della propria arte magica, ottenendo poteri unici e capacità speciali che riflettono il suo approccio alla creazione e all'incantamento degli oggetti"),
                    Create("Lo Strumento più Adatto",3, "Dal 3° Livello l'artefice impara a creare esattamente lo strumento di cui ha bisogno: con gli arnesi da scasso o gli strumenti da artigiano, può realizzare magicamente un set di strumenti da artigiano"),
                    Create("Maestria negli Strumenti",6, "Dal 6° Livello il bonus di competenza dell'artefice ora raddoppia in ogni prova di caratteristica effettuata usando la competenza con uno strumento."),
                    Create("Lampo di Genio",7, "Partendo dal 7° Livello quando una creatura entro 9 m effettua una prova o un tiro salvezza, l'artefice può usare la reazione per aggiungere il suo modificatore di Intelligenza al tiro. Utilizzabile un numero di volte pari al modificatore di Intelligenza, si ricarica con un riposo lungo."),
                    Create("Adepto degli Oggetti Magici",10, "Dal 10° Livello l'artefice raggiunge una profonda conoscenza relativa all'uso e alla creazione di oggetti magici"),
                    Create("Oggetto Che Custodisce Gli Incantesimi",11, "Al 11° Livello dopo un riposo lungo, l'artefice può toccare un'arma o un oggetto non incantato per custodirvi un incantesimo di 1° o 2° livello dalla sua lista."),
                    Create("Sapiente degli Oggetti Magici",14, "Quando l'artefice raggiunge il 14° Livello, può entrare in sintonia con fino a cinque oggetti magici contemporaneamente, ignorando tutti i requisiti di classe, razza, incantesimo e livello necessari per utilizzarli."),
                    Create("Maestro degli Oggetti Magici",18, "L'artefice ora che è al 18° Livello può entrare in sintonia con un massimo di sei oggetti magici contemporaneamente."),
                    Create("Anima Dell'Artificio",20, "Dopo essere arrivato al 20° Livello l'artefice sviluppa una connessione mistica con i suoi oggetti magici, che può utilizzare come protezione."),
                    //alchimista
                    Create("Competenza negli strumenti",3, "Quando adotti questa specializzazione al 3° livello, l'artefice ottiene competenza con gli strumenti da alchimista. Se possiedi già questa competenza, ottieni competenza con un altro tipo di strumento da artigiano a tua scelta."),
                    Create("Incantesimi da alchimista",3, "Dal 3° livello, l'artefice ottiene incantesimi sempre preparati in base al livello di classe. Sono considerati incantesimi da artefice, ma non contano nel numero di incantesimi che puoi preparare."),
                    Create("Elisir sperimentale",3, "Dal 3° livello, dopo un riposo lungo, puoi creare un elisir con effetto casuale. Puoi crearne altri usando slot incantesimo e scegliendo l'effetto. Gli elisir durano fino al prossimo riposo lungo e richiedono scorte da alchimista."),
                    Create("Alchemico Sapiente",5, "Dal 5° livello, quando usi le scorte da alchimista per lanciare un incantesimo che cura o infligge danni da acido, fuoco, necrotico o veleno, aggiungi il tuo modificatore di Intelligenza al tiro (minimo +1)."),
                    Create("Reagenti Ristorativi",9, "Dal 9° livello, chi beve un tuo elisir ottiene 2d6 + Int PF temporanei. Puoi anche lanciare Ristorare Inferiore senza slot né preparazione, usando le scorte da alchimista, un numero di volte pari al tuo modificatore di Intelligenza (minimo 1)."),
                    Create("Maestria nella chimica",15, "Dal 15° livello, l'artefice ottiene resistenza ad acido e veleno, ed è immune all'avvelenamento. Può lanciare Cura Ferite e Ristorare Superiore senza slot, preparazione o componenti, usando le scorte da alchimista. Ogni incantesimo è utilizzabile 1 volta per riposo lungo."),  
                    //armaiolo
                    Create("Strumenti del Mestiere",3, "A partire dal 3° livello, il personaggio ottiene competenza nelle armature pesanti e negli strumenti da fabbro. Se ha già questi ultimi, sceglie una competenza in un altro tipo di strumenti da artigiano."),
                    Create("Incantesimi da armaiolo",3, "Dal 3° livello, l'artefice ottiene incantesimi sempre preparati in base al livello di classe. Sono considerati incantesimi da artefice, ma non contano nel numero di incantesimi che puoi preparare."),
                    Create("Armatura Arcana",3, "Al 3° livello l'artefice può trasformare la propria armatura in armatura arcana usando gli strumenti da fabbro. Non richiede Forza, funge da focus, si lega al corpo e può essere indossata o rimossa solo volontariamente."),
                    Create("Modello di Armatura",3, "A partire dal 3° livello, L'artefice sceglie un modello per la sua armatura arcana: Guardiano o Infiltrato, ottenendo benefici unici e un'arma speciale. Può usare Int al posto di Forza o Destrezza per colpire e fare danni. Può cambiare modello dopo un riposo con strumenti da fabbro."),
                    //-----> attacco extra: vedi sopra
                    Create("Modifiche all'Armatura",9, "Al 9° livello il personaggio impara a usare le infusioni da artefice per modificare in modo specifico l'armatura arcana. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Armatura Perfezionata",15, "Al 15° livello l'armatura arcana del personaggio ottiene vantaggi aggiuntivi in base al suo modello indossato (per maggiori dettagli consultare il manuale: TCOE)"),
                    //artigliere
                    Create("Competenza negli strumenti",3, "Al 3° livello l'artefice ottiene competenza negli strumenti da intagliatore. Se ha gia questa competenza, acquisisce un'altra competenza in un altro tipo di strumento da artigiano a sua scelta."),
                    Create("Incantesimi da artigliere",3, "Dal 3° livello, l'artefice ottiene incantesimi sempre preparati in base al livello di classe. Sono considerati incantesimi da artefice, ma non contano nel numero di incantesimi che puoi preparare."),
                    Create("Cannone Occulto",3, "Al 3° livello, l'artefice può creare un cannone magico (piccolo o minuscolo) con strumenti da intagliatore o fabbro. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Arma  da fuoco arcana",5, "Al 5° livello, puoi trasformare una bacchetta, un bastone o una verga in un'arma da fuoco arcana, usandola come focus. Quando lanci un incantesimo con essa, tiri 1d8 e aggiungi il risultato a un tiro per i danni dell'incantesimo."),
                    Create("Cannone Esplosivo",9, "A partire dal 9° livello, ogni cannone occulto che il personaggio crea ora é più distruttivo. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Posizione Fortificata",15, "Al 15° livello l'artefice diventa un maestro nello strutturare postazioni ben difese usando il suo Cannone Occulto (per maggiori dettagli consultare il manuale: TCOE)"),
                    //fabbro da battaglia
                    Create("Competenza negli strumenti",3, "Al 3° livello l'artefice ottiene competenza negli strumenti da fabbro. Se ha gia questa competenza, acquisisce un'altra competenza in un altro tipo di strumento da artigiano a sua scelta."),
                    Create("Incantesimi da fabbro da battaglia",3, "Dal 3° livello, l'artefice ottiene incantesimi sempre preparati in base al livello di classe. Sono considerati incantesimi da artefice, ma non contano nel numero di incantesimi che puoi preparare."),
                    Create("Pronto per la Battaglia",3, "Al 3° livello l'artefice ottiene competenza nelle armi da guerra. Quando attacchi con un'arma magica, puoi usare il modificatore di Intelligenza al posto di Forza o Destrezza per colpire e per i danni."),
                    Create("Difensore d'Acciaio",3, "A partire dal 3° livello, dopo tanto armeggiare, il personaggio ha creato un compagno per sé: un difensore d'acciaio. È amichevole con lui e con gli altri compagni e obbedisce ai suoi ordini. (per maggiori dettagli sulle sue abilità consultare il manuale: TCOE)"),
                    //-----> attacco extra: vedi sopra
                    Create("Scossa Arcana",9, "Al 9° livello l'artefice ha imparato nuovi modi per incanalare l'energia arcana per ferire o guarire. Quando colpisce un bersaglio con un'arma magica, o il suo difensore d'acciaio colpisce un bersaglio, pud incanalare l'energia magica attraverso il olpo per creare diversi effetti."),
                    Create("Difensore Migliorato",15, "Al 15° livello la Scossa arcana e il Difensore d'acciaio sono più potenti. (per maggiori dettagli consultare il manuale: TCOE)"),

                //BARBARO
                    Create("Ira",1, "Il barbaro entra in Ira con un'azione bonus. In Ira (senza armatura pesante): 1)Vantaggio a prove di Forza e Tiri Salvezza su Forza. 2)Bonus danni agli attacchi con arma da mischia (aumenta con i livelli). 3)Resistenza ai danni contundenti, perforanti e taglienti. 4)Non può lanciare incantesimi. L'Ira dura 1 minuto, termina se cade privo di sensi, non attacca o subisce danni nel turno, o se la interrompe. Può entrare in Ira un numero di volte limitato (tabella), ricaricando con un riposo lungo."),
                    Create("Difesa Senza Armatura",1, "A partire dal 1° livello, finché un barbaro non indossa alcuna armatura, la sua Classe Armatura è pari a 10 + il suo modificatore di Destrezza + il suo modificatore di Costituzione. Un barbaro può usare uno scudo e ottenere comunque questo beneficio."),
                    Create("Attacco Irruento",2,"Al 2° livello, il barbaro può attaccare con furia. Con attacco irruento ha vantaggio ai tiri per colpire in mischia (Forza), ma i tiri contro di lui avranno vantaggio fino al suo turno."),
                    Create("Percezione del Pericolo",2,"Al 2° livello, il barbaro sviluppa una percezione acuta. Ottiene vantaggio ai Tiri Salvezza su Destrezza contro trappole e incantesimi visibili. Per beneficiare di ciò, non deve essere accecato, assordato o incapacitato."),
                    Create("Cammino Primordiale",3,"Al 3° livello, il barbaro sceglie un Cammino Primordiale, definendo la sua furia. Questa scelta offre vantaggi al 3°, 6°, 10° e 14° livello."),
                    Create("Movimento Veloce",5,"A partire dal 5° livello, la velocità del barbaro aumenta di 3 metri purché il barbaro non indossi un'armatura pesante."),
                    Create("Istinto Ferino",7,"Al 7° livello, il barbaro ottiene vantaggio ai tiri per l'iniziativa. Se sorpreso e non incapacitato, può agire normalmente nel primo turno, ma solo entrando in ira per prima."),
                    Create("Critico Brutale",9,"A partire dal 9° livello, il barbaro aggiunge un dado arma extra ai danni critici in mischia. Questo aumenta a 2 dadi al 13° livello e a 3 dadi al 17° livello."),
                    Create("Ira Implacabile",11,"Dall'11° livello, in ira, il barbaro resiste alle ferite. A 0 punti ferita, se non ucciso, tira un TS su Costituzione (CD 10) per rimanere a 1 PF. La CD aumenta di 5 ogni volta che usa questo privilegio, ma torna a 10 dopo un riposo."),
                    Create("Ira Persistente",15,"Dal 15° livello, l'ira del barbaro termina solo se cade privo di sensi o decide di porvi fine"),
                    Create("Potenza Indomabile",18,"A partire dal 18° livello, se il totale di una prova di Forza del barbaro è inferiore al suo punteggio di Forza, il barbaro può usare il suo punteggio di Forza al posto del totale."),
                    Create("Campione Primordiale",20,"Al 20° livello, il barbaro diventa un'incarnazione del potere delle terre selvagge. I suoi punteggi di Forza e Costituzione aumentano di 4. Il suo massimo per quei punteggi diventa 24."),
                    //Cammino del Berserker
                    Create("Frenesia",3, "Al 3° livello, i barbari del Sentiero del Berserker possono entrare in frenesia durante l'ira. In frenesia, possono effettuare un attacco con arma da mischia come azione bonus in ogni turno. Quando la frenesia termina, il barbaro subisce un livello di sfinimento."),
                    Create("Frenesia Insensata",6, "A partire dal 6° livello, il barbaro non può essere affascinato o spaventato mentre è in ira. Se affascinato o spaventato prima di entrare in ira, l'effetto si sospende per la durata dell'ira."),
                    Create("Presenza Intimidatoria",10, "Al 10° livello, il barbaro può usare l'azione per spaventare una creatura entro 9 metri. La creatura deve superare un tiro salvezza su Saggezza (CD = 8 + bonus competenza + modificatore Carisma) o essere spaventata fino alla fine del turno successivo. L'effetto può essere esteso, ma termina se la creatura è fuori dalla linea di vista o a più di 18 metri. Se la creatura supera il tiro salvezza, non può essere bersagliata di nuovo per 24 ore."),
                    Create("Ritorsione",14, "Al 14° livello, quando subisci danni da una creatura entro 1,5 metri, puoi usare la tua reazione per effettuare un attacco con arma da mischia contro quella creatura."),
                    //Cammino del combattente totemico
                    Create("Cercatore di Spiriti",3,"Al 3° livello, i druidi che seguono questo cammino ottengono la capacità di lanciare gli incantesimi Percezione Bestiale e Parlare con gli Animali, ma solo come rituali. Questo percorso enfatizza l'armonia con il mondo naturale e le creature che lo abitano."),
                    Create("Spirito Totemico",3,"Al 3° livello, i barbari del cammino totemico scelgono uno spirito totemico e ne ottengono le caratteristiche. Necessitano di un oggetto totemico fisico. Possono anche avere attributi fisici minori legati allo spirito. 1) Orso: Resistenza a tutti i danni tranne psichici durante la furia. 2) Aquila: Svantaggio ai tiri per attacco di opportunità contro di te e puoi usare Scattare come azione bonus (senza armatura pesante). 3) Alce: Aumento di 4,5 metri alla velocità sul passo (senza armatura pesante). 4) Tigre: Aumento delle distanze dei salti. 5) Lupo: I tuoi alleati hanno vantaggio ai tiri per colpire in mischia contro nemici entro 1,5 metri."),
                    Create("Aspetto della Bestia",6,"Al 6° livello, scegli un beneficio magico basato sul tuo animale totemico: 1) Orso: Capacità di trasporto raddoppiata, vantaggio alle prove di Forza per spingere/tirare/sollevare. 2) Aquila: Vista di 1,6 km, luce fioca non impone svantaggio a Percezione. 3) Alce: Velocità di viaggio raddoppiata (e per 10 compagni entro 18 metri). 4) Tigre: Competenza in due abilità (Atletica, Acrobazia, Furtività, Sopravvivenza). 5) Lupo: Puoi seguire le tracce a passo veloce, muoverti furtivamente a passo normale."),
                    Create("Viandante Spirituale",10,"Al 10° livello, puoi lanciare l'incantesimo Comunione con la Natura come rituale. Una versione spirituale dell'animale totemico scelto ti apparirà per darti le informazioni che cerchi."),
                    Create("Sintonizzazione Totemica",14,"Al 14° livello, ottieni un beneficio magico basato sull'animale totemico: 1) Orso: I nemici entro 1,5 metri hanno svantaggio ai tiri per colpire (tranne contro di te). 2) Aquila: Velocità di volo pari alla tua velocità di camminata durante la furia. 3) Alce: Puoi attraversare lo spazio di creature Grandi o inferiori, facendole cadere e subendo danni. 4) Tigre: Attacco aggiuntivo con arma da mischia dopo esserti mosso di almeno 6 metri. 5) Lupo: Puoi far cadere prona una creatura Grande o inferiore con un attacco con arma da mischia."),
                    //Cammino del guardiano ancestrale
                    Create("Protettori Ancestrali",3,"Al 3° livello, quando entri in ira, appaiono guerrieri spettrali. Il primo bersaglio colpito nel tuo turno subisce svantaggio ai tiri per colpire (tranne contro di te) e le creature colpite dal bersaglio hanno resistenza ai danni. L'effetto termina se la tua ira finisce."),
                    Create("Scudo Spirituale",6,"Al 6° livello, durante la furia, puoi ridurre i danni subiti da una creatura entro 9 metri usando la tua reazione: 1) 6° livello: Riduzione di 2d6 danni. 2) 10° livello: Riduzione di 3d6 danni. 3) 14° livello: Riduzione di 4d6 danni."),
                    Create("Consultare gli Spiriti",10,"Al 10° livello, puoi consultare gli spiriti ancestrali. Lanci Presagio o Chiaroveggenza senza slot incantesimo o componenti. Chiaroveggenza evoca uno spirito ancestrale. La tua caratteristica da incantatore è Saggezza. Dopo aver usato questa capacità, devi riposare brevemente o a lungo prima di poterla usare di nuovo."),
                    Create("Antenati Vendicativi",14,"Al 14° livello, quando usi Scudo Spirituale per ridurre i danni, l'attaccante subisce danni da forza pari alla quantità di danni prevenuti."),
                    //Cammino dell'araldo della tempesta
                    Create("Aura di Tempesta",3,"Al 3° livello, ottieni un'aura durante la furia (raggio di 3 metri). Puoi scegliere tra: 1) Deserto: Danni da fuoco (2, aumenta al 5°, 10°, 15°, 20° livello). 2) Mare: Tiro salvezza su Destrezza per danni da fulmine (1d6, aumenta al 10°, 15°, 20° livello). 3) Tundra: Punti ferita temporanei (2, aumenta al 5°, 10°, 15°, 20° livello). Puoi cambiare l'ambiente ad ogni livello. La CD dei tiri salvezza è 8 + bonus di competenza + modificatore di Costituzione."),
                    Create("Anima della Tempesta",6,"Al 6° livello, ottieni benefici basati sull'ambiente scelto: 1) Deserto: Resistenza al fuoco, immunità al calore estremo, puoi incendiare oggetti con un'azione. 2) Mare: Resistenza ai fulmini, respiri sott'acqua, velocità di nuoto di 9 metri. 3) Tundra: Resistenza al freddo, immunità al freddo estremo, puoi congelare l'acqua (1,5 metri cubi) con un'azione."),
                    Create("Tempesta Protettiva",10,"Al 10° livello, le creature nella tua Aura della Tempesta ottengono la tua stessa resistenza ai danni ottenuta da Anima della Tempest"),
                    Create("Tempesta Furiosa",14,"Al 14° livello, la tua Aura della Tempesta infligge effetti aggiuntivi: 1) Deserto: Dopo un attacco subito, reazione per tiro salvezza su Destrezza per danni da fuoco (metà del tuo livello da Barbaro). 2) Mare: Dopo un attacco, reazione per tiro salvezza su Forza per far cadere prona la creatura. 3) Tundra: Quando l'aura si attiva, tiro salvezza su Forza per ridurre la velocità a 0 fino al tuo prossimo turno."),
                    //Cammino dello Zelota
                    Create("Furia Divina",3,"Al 3° livello, mentre sei in preda all'ira, il tuo primo attacco con un'arma in ogni turno infligge danni extra (1d6 + metà del tuo livello da Barbaro). Scegli tra danni necrotici o radiosi."),
                    Create("Guerriero degli Dei",3,"Al 3° livello, gli incantesimi che ti riportano in vita (esclusi quelli di non-morte) non richiedono componenti materiali."),
                    Create("Focus Fanatico",6,"Al 6° livello, puoi ritirare un tiro salvezza fallito mentre sei in preda all'ira, usando il nuovo risultato. Puoi usare questa capacità una volta per furia."),
                    Create("Presenza Zelante",10,"Al 10° livello, come azione bonus, lanci un grido di battaglia. Fino a dieci creature entro 18 metri ottengono vantaggio ai tiri per colpire e ai tiri salvezza fino all'inizio del tuo turno successivo. Questa capacità può essere usata una volta per riposo lungo."),
                    Create("Ira Oltre la Morte",14,"Al 14° livello, in preda all'ira, non cadi inconscio a 0 punti ferita. Devi comunque effettuare tiri salvezza su morte e subisci gli effetti dei danni. Se muori per fallimento dei tiri salvezza su morte, muori solo quando l'ira finisce, e se hai ancora 0 punti ferita."),
                    //Cammino della Bestia
                    Create("Forma della Bestia",3,"Al 3° livello, in preda all'ira, puoi trasformarti, manifestando un'arma naturale. Scegli tra: 1) Morso: 1d8 danni perforanti. Recuperi punti ferita (pari al tuo bonus di competenza) se hai meno della metà dei tuoi punti ferita. 2) Artigli: 1d6 danni taglienti. Attacco aggiuntivo con artiglio. 3) Coda: 1d8 danni perforanti, portata. Reazione: colpisci con la coda se vieni colpito, aumentando la CA."),
                    Create("Anima Bestiale",6,"Al 6° livello, le tue armi naturali sono considerate magiche. Dopo un riposo breve/lungo, scegli un beneficio: 1) Acqua: Velocità di nuoto e respirazione subacquea. 2) Arrampicata: Velocità di scalata. 3) Salto: Prova di Forza (Atletica) per aumentare la distanza del salto (una volta per turno)."),
                    Create("Furia Infettiva",10,"Al 10° livello, quando colpisci con le armi naturali in ira, puoi maledire il bersaglio (TS Saggezza, CD = 8 + mod. Costituzione + bonus competenza). Scegli un effetto: 1) Attacco di reazione contro una creatura a tua scelta. 2) 2d12 danni psichici. 3) Puoi usare questa capacità un numero di volte pari al tuo bonus di competenza, recuperando gli utilizzi con un riposo lungo."),
                    Create("Chiamata alla Caccia",14,"Al 14° livello, quando entri in ira, puoi condividere la tua ferocia. Scegli creature consenzienti (fino al tuo mod. Costituzione) entro 9 metri. Ottieni 5 pf temporanei per creatura. Le creature scelte, una volta per turno, possono tirare un d6 e aggiungere il risultato ai danni. Puoi usare questa capacità un numero di volte pari al tuo bonus di competenza, recuperando gli utilizzi con un riposo lungo."),
                    //Cammino della Magia Selvatica
                    Create("Consapevolezza Magica",3,"Al 3° livello, come azione, puoi percepire la magia. Fino alla fine del tuo prossimo turno, conosci la posizione di incantesimi e oggetti magici entro 18 metri (non sotto copertura totale). Scopri la scuola di magia dell'incantesimo. Puoi usare questa capacità un numero di volte pari al tuo bonus di competenza, recuperando gli utilizzi con un riposo lungo."),
                    Create("Impeto Selvaggio",3,"Al 3° livello, quando entri in ira, la magia Selvatica erutta. Tira sulla tabella Magia Selvatica per determinare l'effetto. Se è richiesto un tiro salvezza, la CD è pari a 8 + bonus di competenza + modificatore di Costituzione. Consultare il Manuale: TCOE"),
                    Create("Magia Potenziante",6,"Al 6° livello, puoi potenziare te stesso o un alleato. Con un'azione, puoi conferire un beneficio: 1) D3 per i tiri: Per 10 minuti, aggiungi un d3 ai tiri per colpire e alle prove di caratteristica. 2) Recupero slot incantesimo: Tira un d3. Recupera uno slot incantesimo (livello pari o inferiore al risultato). 3) Puoi usare questa azione un numero di volte pari al tuo bonus di competenza, recuperando gli utilizzi con un riposo lungo."),
                    Create("Contracco Instabile",10,"Al 10° livello, quando sei in ira e subisci danni o fallisci un tiro salvezza, puoi reagire. Tira sulla tabella Magia Selvatica per un effetto immediato, che sostituisce l'effetto attuale."),
                    Create("Impulso Controllato",14,"Al 14° livello, quando tiri sulla tabella Magia Selvatica, tira due volte e scegli l'effetto. Se ottieni lo stesso numero su entrambi i dadi, puoi scegliere qualsiasi effetto."),

                //BARDO
                    Create("Incantesimi",1,"A partire dal 1° livello, il bardo manipola la realtà con la musica. I suoi incantesimi sono parte del suo repertorio, adattabili a diverse situazioni, dimostrando la sua abilità nel districare e rimodellare la trama della realtà attraverso l'arte."),
                    Create("Ispirazione Bardica",1,"Il bardo ispira gli altri con musica/parole. Usa un'azione bonus per dare a un alleato (entro 18m) un dado di Ispirazione Bardica (d6, aumenta a d8/d10/d12 con i livelli). L'alleato usa il dado entro 10 minuti per aggiungere il risultato a una prova. Il bardo usa questo privilegio Modificatore Carisma volte (min. 1), ricaricando con un riposo lungo."),
                    Create("Factotum",2,"A partire dal 2° livello, un bardo può aggiungere metà del suo bonus di competenza, arrotondato per difetto, a ogni prova di caratteristica da lui effettuata che non includa già il suo bonus di competenza."),
                    Create("Canto di Riposo",2,"Dal 2° livello, il bardo usa musica curativa durante i riposi brevi. Se gli alleati spendono Dadi Vita, recuperano 1d6 PF extra (aumenta a 1d8 al 9°, 1d10 al 13° e 1d12 al 17° livello)."),
                    Create("Collegio Bardico",3,"Al 3° livello, un bardo apprende le tecniche più avanzate di un collegio bardico a sua scelta come ad esempio il Collegio della Sapienza. Il collegio a sua scelta gli conferisce alcuni privilegi al 3° livello e poi di nuovo al 6° e al 14° livello."),
                    Create("Maestria",3,"Al 3° livello, un bardo sceglie due tra le sue competenze nelle abilità. Il suo bonus di competenza raddoppia in ogni prova di caratteristica effettuata usando una delle competenza scelte. Al 10° livello, un bardo può scegliere altre due competenze nelle abilità per ottenere questo beneficio."),
                    Create("Fonte di Ispirazione",5,"A partire dal 5° livello, un bardo recupera tutti i suoi utilizzi spesi di Ispirazione Bardica quando completa un riposo breve o lungo."),
                    Create("Controfascino",6,"Al 6° livello, il bardo usa musica per proteggere dagli effetti mentali. Con un'azione, inizia un'esibizione (fino al suo turno successivo) che dà vantaggio ai tiri salvezza contro Affascinamento/Paura a sé e agli alleati entro 9 metri. Funziona solo se possono sentire il bardo. Termina se il bardo è incapacitato, silenziato o decide di interromperla."),
                    Create("Segreti Magici",10,"Al 10° livello, il bardo impara incantesimi di altre classi. Sceglie 2 incantesimi di qualsiasi classe (o trucchetti) di livello lanciabile. Contano come incantesimi da bardo. Apprende 2 incantesimi aggiuntivi di altre classi al 14° e al 18° livello."),
                    Create("Ispirazione Superiore",20,"Al 20° livello, quando il bardo tira per l'iniziativa e non gli rimane alcun utilizzo di Ispirazione Bardica, ne recupera un utilizzo."),
                    //Collegio del Incanto
                    Create("Manto d'Ispirazione",3,"Al 3° livello, i bardi del Collegio del Fascino possono usare l'Ispirazione Bardica come azione bonus. Scegli creature (fino al tuo mod. Carisma) entro 18 metri. Ottengono punti ferita temporanei (5 al 3° livello, 8 al 5°, 11 al 10°, 14 al 15°) e possono usare la reazione per muoversi senza provocare attacchi di opportunità."),
                    Create("Esibizione Estasiante",3,"Al 3° livello, i bardi del Collegio del Fascino possono ammaliarsi. Dopo un'esibizione di 1 minuto, scegli umanoidi (fino al tuo mod. Carisma) entro 18 metri. Devono superare un tiro salvezza su Saggezza (CD del tuo tiro salvezza degli incantesimi) o essere affascinati. Gli affascinati ti idolatrano per 1 ora, a meno che non subiscano danni, o tu attacchi loro o i loro alleati. Un fallimento al tiro salvezza non lascia indizi. Usi questa capacità una volta per riposo."),
                    Create("Manto di Maestà",6,"Al 6° livello, i bardi del Collegio del Fascino ottengono Comando irresistibile. Come azione bonus, lanciano Comando (senza slot incantesimo) e diventano ultraterreni per 1 minuto (concentrazione). In ogni turno, possono lanciare Comando come azione bonus. Le creature affascinate falliscono automaticamente i tiri salvezza contro Comando. Usi questa capacità una volta per riposo lungo."),
                    Create("Maestà Inviolabile",14,"Al 14° livello, i bardi del Collegio del Fascino hanno un aspetto ultraterreno permanente. Come azione bonus, possono assumere una presenza maestosa per 1 minuto. Gli attaccanti devono superare un tiro salvezza su Carisma (CD del tuo tiro salvezza degli incantesimi) o non possono attaccarti. Se superano, subiscono svantaggio ai tiri salvezza contro i tuoi incantesimi nel tuo turno successivo. Usi questa capacità una volta per riposo."),
                    //Collegio della sapienza
                    Create("Competenze Bonus",3,"Quando ti unisci al Collegio del Sapere al 3° livello, ottieni competenza in tre abilità a tua scelta."),
                    Create("Parole Taglienti",3,"Al 3° livello, i bardi del Collegio del Sapere possono usare la reazione per Sarcasmo tagliente. Quando una creatura entro 18 metri effettua un tiro per colpire, una prova di caratteristica o un tiro per i danni, spendi un uso di Ispirazione Bardica, tiri il dado di Ispirazione Bardica e sottrai il risultato dal tiro della creatura. Puoi usarlo dopo il tiro, ma prima che il DM determini il successo/fallimento o prima che la creatura infligga i danni. La creatura è immune se non può sentirti o è immune all'essere affascinata."),
                    Create("Segreti Magici Aggiuntivi",6,"Al 6° livello, i bardi del Collegio del Sapere imparano due incantesimi a scelta da qualsiasi classe. Un incantesimo deve essere di un livello che puoi lanciare (come mostrato nella tabella del Bardo) o un trucchetto. Questi incantesimi contano come incantesimi da bardo, ma non influiscono sul numero di incantesimi da bardo che conosci."),
                    Create("Abilità Impareggiabile",14,"Al 14° livello, i bardi del Collegio del Sapere ottengono Maestria. Quando effettui una prova di caratteristica, puoi spendere un uso di Ispirazione Bardica. Tira il dado di Ispirazione Bardica e aggiungi il risultato alla tua prova di caratteristica. Puoi farlo dopo aver tirato il dado per la prova di caratteristica, ma prima che il DM ti dica se hai superato o fallito."),
                    //Collegio del valore
                    Create("Competenze Bonus",3,"Al 3° livello, i bardi del Collegio del Valore ottengono competenza con armature medie, scudi e armi da guerra."),
                    Create("Ispirazione in Combattimento",3,"Al 3° livello, i bardi del Collegio del Valore ottengono Ispirazione in battaglia. Una creatura con un dado di Ispirazione Bardica da te può tirare quel dado e aggiungere il risultato a un tiro per i danni con l'arma. In alternativa, quando viene effettuato un tiro per colpire contro la creatura, questa può usare la sua reazione per tirare il dado di Ispirazione Bardica e aggiungere il risultato alla sua CA contro quell'attacco."),
                    Create("Attacco Extra",6,"A partire dal 6° livello, puoi attaccare due volte, invece di una, ogni volta che esegui l'azione di Attacco nel tuo turno."),
                    Create("Magia da Battaglia",14,"Al 14° livello, i bardi del Collegio del Valore ottengono Maestria in battaglia. Quando usi la tua azione per lanciare un incantesimo da bardo, puoi effettuare un attacco con l'arma come azione bonus."),
                    //Collegio dei sussurri
                    Create("Lame Psichiche",3,"Al 3° livello, i bardi del Collegio dei Sussurri ottengono Lama del Sussurro. Quando colpisci una creatura con un attacco con un'arma, puoi spendere un uso della tua Ispirazione Bardica per infliggere 2d6 danni psichici aggiuntivi a quel bersaglio. Puoi farlo solo una volta per round. I danni aumentano a 3d6 al 5° livello, 5d6 al 10° livello e 8d6 al 15° livello."),
                    Create("Parole del Terrore",3,"Al 3° livello, i bardi del Collegio dei Sussurri ottengono Parole del Terrore. Se parli da solo con un umanoide per almeno 1 minuto, puoi tentare di seminare paranoia e paura nella sua mente. Il bersaglio deve superare un tiro salvezza su Saggezza contro la CD del tuo tiro salvezza degli incantesimi o essere spaventato per 1 ora. Se il bersaglio supera il tiro salvezza, non ha alcun indizio. Non puoi usare di nuovo questa capacità finché non completi un riposo."),
                    Create("Manto dei Sussurri",6,"Al 6° livello, i bardi del Collegio dei Sussurri ottengono Ombra Rubata. Quando un umanoide muore entro 9 metri da te, puoi catturare magicamente la sua ombra usando la tua reazione. Puoi usare l'ombra come azione per assumere l'aspetto della persona morta per 1 ora. Ottieni accesso a informazioni che l'umanoide condividerebbe liberamente. Un'altra creatura può vedere attraverso questo travestimento superando una prova di Saggezza (Intuizione) contesa dalla tua prova di Carisma (Inganno) + 5. Non puoi catturare un'altra ombra finché non completi un riposo."),
                    Create("Sapienza delle Ombre",14,"Al 14° livello, i bardi del Collegio dei Sussurri ottengono Segreto Svelato. Come azione, sussurri una frase che solo una creatura entro 9 metri può sentire. Il bersaglio deve effettuare un tiro salvezza su Saggezza contro la CD del tuo tiro salvezza. Se fallisce, è affascinato da te per 8 ore e interpreta i sussurri come la descrizione del suo segreto più mortificante. La creatura obbedisce ai tuoi comandi per paura che tu riveli il suo segreto. Non puoi usare di nuovo questa capacità finché non completi un riposo."),
                    //Collegio dell'eloquenza 
                    Create("Lingua d'Argento",3,"A partire dal 3° livello, sei un maestro nel dire la cosa giusta al momento giusto. Quando effettui una prova di Carisma (Persuasione) o Carisma (Inganno), puoi considerare un risultato di 9 o inferiore sul d20 come un 10."),
                    Create("Parole Inquietanti",3,"Al 3° livello, i bardi del Collegio della Conoscenza ottengono Incoraggiamento Intimidatorio. Come azione bonus, puoi spendere un uso della tua Ispirazione Bardica e scegliere una creatura entro 18 metri. Tira il dado di Ispirazione Bardica. La creatura deve sottrarre il numero ottenuto dal prossimo tiro salvezza che effettua prima dell'inizio del tuo prossimo turno."),
                    Create("Ispirazione Infallibile",6,"Al 6° livello, i bardi del Collegio della Conoscenza ottengono Ispirazione di Conoscenza. Quando una creatura aggiunge uno dei tuoi dadi di Ispirazione Bardica e il tiro fallisce, la creatura può tenere il dado di Ispirazione Bardica."),
                    Create("Favella Universale",6,"Al 6° livello, i bardi del Collegio della Conoscenza ottengono Comprensione Universale. Come azione, scegli una o più creature entro 18 metri, fino a un numero pari al tuo modificatore di Carisma. Le creature scelte possono capirti magicamente, indipendentemente dalla lingua che parli, per 1 ora. Non puoi usare di nuovo questa capacità finché non completi un riposo lungo, a meno che tu non spenda uno slot incantesimo."),
                    Create("Ispirazione Contagiosa",14,"Al 14° livello, i bardi del Collegio della Conoscenza ottengono Ispirazione Rinforzata. Quando una creatura entro 18 metri aggiunge un tuo dado di Ispirazione Bardica e il tiro riesce, puoi usare la tua reazione per incoraggiare un'altra creatura entro 18 metri, assegnandole un dado di Ispirazione Bardica. Puoi usare questa reazione un numero di volte pari al tuo modificatore di Carisma (minimo una volta) e recuperi tutti gli usi spesi al termine di un riposo lungo."),
                    //"Collegio della creazione"
                    Create("Granulo di Potenziale",3,"Al 3° livello, i bardi del Collegio della Creazione ottengono Creazione di Potenziale. Ogni volta che assegni un dado di Ispirazione Bardica, crei un granello di potenziale che orbita entro 1,5 metri dalla creatura. Prova di Caratteristica: La creatura può tirare di nuovo il dado. Tiro per Colpire: Il bersaglio e le creature entro 1,5 metri devono superare un tiro salvezza su Costituzione o subire danni da tuono. Tiro salvezza: La creatura ottiene punti ferita temporanei."),
                    Create("Esecuzione della Creazione",3,"Al 3° livello, i bardi del Collegio della Creazione ottengono Creazione di un'Opera. Come azione, puoi creare un oggetto non magico entro 3 metri. Il valore in mo non può superare 20 volte il tuo livello da bardo, e l'oggetto deve essere di taglia Media o inferiore. L'oggetto scompare dopo un numero di ore pari al tuo bonus di competenza. Non puoi farlo di nuovo finché non completi un riposo lungo, a meno che tu non spenda uno slot incantesimo. La taglia aumenta al 6° (Grande) e al 14° livello (Enorme)."),
                    Create("Animare una Performance",6,"Al 6° livello, i bardi del Collegio della Creazione ottengono Animare Oggetto. Come azione, puoi animare un oggetto non magico di taglia Grande o inferiore entro 9 metri. L'oggetto usa le statistiche di Oggetto Danzante e obbedisce ai tuoi comandi per 1 ora. Condivide la tua iniziativa, ma agisce dopo di te. Può eseguire solo l'azione Schivare a meno che tu non usi un'azione bonus per comandarlo. Non puoi farlo di nuovo finché non completi un riposo lungo, a meno che tu non spenda uno slot incantesimo."),
                    Create("Crescendo Creativo",14,"Al 14° livello, i bardi del Collegio della Creazione migliorano Esibizione della Creazione. Quando usi questa capacità, puoi creare un numero di oggetti pari al tuo modificatore di Carisma (minimo due). Solo uno può essere della taglia massima. Non sei più limitato dal valore in mo."),
                    //Collegio della spada
                    Create("Competenze Bonus",3,"Al 3° livello, i bardi del Collegio delle Spade ottengono: 1)Competenza nell'armatura media e nella scimitarra. 2) Possono usare armi da mischia semplici o da guerra come focus di lancio per gli incantesimi da bardo."),
                    Create("Stile di Combattimento",3,"Al 3° livello, i bardi del Collegio delle Spade ottengono uno Stile di Combattimento: 1) Duello: +2 ai danni con armi da mischia impugnate in una mano. 2) Combattimento con Due Armi: Aggiungi il modificatore di caratteristica al danno del secondo attacco. Inoltre, ottengono Fioritura della Lama. Quando attacchi, la velocità di camminata aumenta di 3 metri. Se colpisci, puoi usare un'opzione di Fioritura della Lama (spendendo Ispirazione Bardica): 1) Fiorescenza Difensiva: Danno extra e bonus alla CA. 2) Fiorescenza Tagliente: Danno extra al bersaglio e alle creature vicine. 3) Fiorescenza Mobile: Danno extra, spingi il bersaglio e ti muovi."),
                    Create("Attacco Extra",6,"A partire dal 6° livello, puoi attaccare due volte, invece di una, ogni volta che esegui l'azione Attacco nel tuo turno."),
                    Create("Fiore della Maestra",14,"A partire dal 14° livello, ogni volta che usi l'opzione Fioritura della Lama, puoi tirare un d6 e usarlo invece di spendere un dado di Ispirazione Bardica."),
                    
                //CHIERICO
                    Create("Incantesimi",1, "A partire dal 1° livello, il chierico è un tramite del potere divino e può lanciare incantesimi della lista del chierico."),
                    Create("Dominio Divino", 1,"Al 1° livello il chierico sceglie un dominio legato alla sua divinità. Il dominio fornisce incantesimi bonus e privilegi speciali, oltre a opzioni aggiuntive per Incanalare Divinità al 2° livello e benefici extra al 6°, 8° e 17° livello."),
                    Create("Incanalare Divinità",2, "Al 2° livello il chierico ottiene la capacità di incanalare l'energia della divinità per attivare potenti effetti magici. (per maggiori dettagli sugli effetti e sul loro utilizzo consultare il manuale: PHB)"),
                    Create("Imbrigliare il poter Divino (Opzionale)",2, "Al 2° livello il chierico può spendere un utilizzo del privilegio Incanalare Divinità per alimentare i suoi incantesimi. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Trucchetti Versatili (Opzionale)",4, "Al livello 4° ogni volta che il chierico un livello in questa classe che garantisce un anumenti di punteggi di caratteristica, può sostituire un trucchetto che ha appreso."),
                    Create("Distruggere non morti(GS 1/2)", 5,"A partire dal 5° livello, quando un non morto fallisce il suo  tiro salvezza contro il privilegio Scacciare Non Morti del chierico, quella creatura è distrutta istantaneamente: se  il suo grado di sfida è pari o inferiore al GS 1/2"),
                    Create("Distruggere non morti(GS 1)",8, "All'8° livello, quando un non morto fallisce il suo  tiro salvezza contro il privilegio Scacciare Non Morti del chierico, quella creatura è distrutta istantaneamente: se  il suo grado di sfida è pari o inferiore al GS 1"),
                    Create("Intervento Divino",10, "A partire dal 10° livello, il chierico può appellarsi alla propria divinità affinchè intervenga a suo beneficio nel momento del bisogno. Puoi chiedere aiuto divino tirando il dado percentuale d100: se il risultato è pari o inferiore al tuo livello, la divinità interviene. Al 20° livello, ha sempre successo. Dopo un successo, non puoi riutilizzarlo per 7 giorni."),
                    Create("Distruggere non morti(GS 2)",11, "All'11° livello, quando un non morto fallisce il suo  tiro salvezza contro il privilegio Scacciare Non Morti del chierico, quella creatura è distrutta istantaneamente: se  il suo grado di sfida è pari o inferiore al GS 2"),
                    Create("Distruggere non morti(GS 3)",14, "Al 14° livello, quando un non morto fallisce il suo  tiro salvezza contro il privilegio Scacciare Non Morti del chierico, quella creatura è distrutta istantaneamente: se  il suo grado di sfida è pari o inferiore al GS 3"),
                    Create("Distruggere non morti(GS 4)",17, "Al 17° livello, quando un non morto fallisce il suo  tiro salvezza contro il privilegio Scacciare Non Morti del chierico, quella creatura è distrutta istantaneamente: se  il suo grado di sfida è pari o inferiore al GS 4"),    
                    // Dominio della Morte
                    Create("Incantesimi del Dominio della Morte",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Competenza Bonus",1, "Quando sceglie questo dominio al 1° livello, il chierico acquisisce competenza con le armi marziali."),
                    Create("Mietitore",1, "Al 1° livello, il chierico può colpire due creature vicine con Trucchetti di necromanzia a bersaglio singolo."),
                    Create("Incanalare divinità: Tocco della Morte",2, "Dal 2° livello, il chierico infligge danni necrotici extra pari a 5 + 2x il suo livello con un attacco in mischia."),
                    Create("Distruzione Inevitabile", 6,"Dal 6° livello, i danni necrotici del chierico ignorano resistenze necrotiche."),
                    Create("Colpo Divino", 8,"All'8° livello, il chierico infligge +1d8 necrotico con un attacco d'arma per turno (2d8 al 14° livello)."),
                    Create("Mietitore Migliorato", 17,"Dal 17° livello, gli incantesimi di necromanzia a bersaglio singolo colpiscono due creature vicine (entro 1,5m)."),
                    // Dominio della Forgia
                    Create("Incantesimi del Dominio della Forgia",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Competenza Bonus",1, "Al 1° livello, il chierico ottiene competenza in armature pesanti e strumenti da fabbro."),
                    Create("Benedizione della Forgia", 1,"Al 1° livello, il chierico può rendere magica un'arma o armatura con bonus +1. Una volta per riposo lungo."),
                    Create("Incanalare Divinità: Benedizione dell'Artigiano",2, "Dal 2° livello, il chierico può creare oggetti metallici non magici con un rituale di 1 ora."),
                    Create("Anima della Forgia",6, "Dal 6° livello, il chierico ottiene resistenza al fuoco e +1 alla Classe Armatura se indossa armatura pesante."),
                    Create("Colpo Divino",8, "All'8° livello, il chierico infligge +1d8 fuoco con un attacco d'arma (2d8 al 14° livello)."),
                    Create("Santo della Forgia e del Fuoco",17, "Dal 17° livello, il chierico è immune al fuoco e ottiene resistenza ai danni fisici non magici con armatura pesante."),
                    // Dominio della Tomba
                    Create("Incantesimi del Dominio della Tomba",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Cerchio di Mortalità",1, "Al 1° livello, il chierico cura con il massimo dei dadi chi è a 0 PF, impara Stabilizzare con gittata 9m e come bonus."),
                    Create("Occhi della Tomba",1, "Al 1° livello, il chierico rileva non morti entro 18m come azione. Usi limitati pari al modificatore di Saggezza, ricarica con riposo lungo."),
                    Create("Incanalare Divinità: Verso la Tomba",2, "Dal 2° livello, il chierico maledice un nemico: subisce vulnerabilità al prossimo attacco entro 1 turno."),
                    Create("Sentinella alla Porta della Morte",6, "Dal 6° livello, il chierico può annullare un critico su sé stesso o un alleato entro 9m, trasformandolo in un colpo normale. Il chierico può usare questo privilegio un numero di volte pari al suo modificatore di Saggezza."),
                    Create("Incantesimi Potenti", 8,"Dall'8° livello, il chierico aggiunge il suo modificatore di Saggezza ai danni dei suoi trucchetti da chierico."),
                    Create("Custode delle Anime", 17,"Dal 17° livello, il chierico cura sé stesso o un alleato con i Dadi Vita di un nemico morto entro 9m. Una volta per turno."),
                    // Dominio della Conoscenza
                    Create("Incantesimi del Dominio della Conoscenza",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Benedizione della Conoscenza",1, "Al 1° livello, il chierico impara 2 lingue e ottiene 2 competenze, raddoppiando il bonus per quelle abilità."),
                    Create("Incanalare Divinità: Conoscenze Secolari",2, "Dal 2° livello, il chierico ottiene competenza temporanea in un'abilità o strumento per 10 minuti."),
                    Create("Incanalare Divinità: Lettura del Pensiero",6, "Dal 6° livello, il chierico legge i pensieri per 1 minuto e può lanciare Suggestione senza slot."),
                    Create("Incantesimi Potenti",8, "Dall'8° livello, il chierico aggiunge il suo modificatore di Saggezza ai danni dei trucchetti da chierico."),
                    Create("Visioni del Passato",17, "Dal 17° livello, il chierico medita per vedere eventi legati a un oggetto o area. (per maggiori dettagli consultare il manuale: PHB)"),
                    // Dominio della Vita
                    Create("Incantesimi del Dominio della Vita",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Competenza Bonus",1, "Al 1° livello, il chierico ottiene competenza nelle armature pesanti."),
                    Create("Discepolo della Vita",1, "Al 1° livello, le cure dei trucchetti del chierico guariscono + 2 + livello dell'incantesimo."),
                    Create("Incanalare Divinità: Preservare Vita",2, "Dal 2° livello, il chierico distribuisce cure pari a 5x livello tra le creature entro 9m. Cura al massimo la metà PF di una creatura."),
                    Create("Guaritore Benedetto",6, "Dal 6° livello, ogni cura su altri guarisce anche il chierico di 2 + livello dell'incantesimo."),
                    Create("Colpo Divino",8, "Dall'8° livello, il chierico infligge +1d8 danni radianti con armi, +2d8 al 14° livello."),
                    Create("Guarigione Suprema",17, "Dal 17° livello, tutte le cure usano sempre il valore massimo di ogni dado."),
                    // Dominio della Luce
                    Create("Incantesimi del Dominio della Luce",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Trucchetto Bonus",1, "Al 1° livello, il chierico impara il trucchetto Luce. Non conta nei trucchetti conosciuti."),
                    Create("Lampo di Interdizione",1, "Al 1° livello, il chierico può imporre svantaggio a un attacco contro di lui entro 9m come reazione. Utilizzabile fino al modificatore di Saggezza per riposo lungo."),
                    Create("Incanalare Divinità: Fulgore dell'Alba",2, "Dal 2° livello, il chierico disperde oscurità magica entro 9m e ogni creatura ostile entro 9m deve effettuare un tiro salvezza su Costituzione. Se fallisce, subisce 2d10 + il livello del chierico danni radiosi, altrimenti subisce solo metà danno."),
                    Create("Lampo Migliorato",6, "Dal 6° livello, il chierico può usare Lampo di Interdizione anche per difendere altri entro 9m."),
                    Create("Incantesimi Potenti",8, "Dall'8° livello, il chierico aggiunge il suo modificatore di Saggezza ai danni dei suoi trucchetti da chierico."),
                    Create("Corona di Luce",17, "Dal 17° livello, il chierico emette un'aura solare per 1 minuto (18m luce brillante, 9m fioca). I nemici nella luce brillante hanno svantaggio ai TS contro incantesimi da fuoco o radianza."),
                    // Dominio della Natura
                    Create("Incantesimi del Dominio della Natura",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Acolito della Natura",1, "Al 1° livello, il chierico impara un trucchetto da druido (non conta nei trucchetti conosciuti). Ottiene competenza in Addestrare Animali, Natura o Sopravvivenza."),
                    Create("Competenza Bonus",1, "Al 1° livello, il chierico ottiene competenza nelle armature pesanti."),
                    Create("Incanalare Divinità: Charme su Animali e Vegetali",2, "Dal 2° livello, il chierico può ammaliare bestie e creature vegetali  in grado di vederlo entro 9m. TS Saggezza o sono ammaliate per 1 minuto o finché non subiscono danni."),
                    Create("Attenuare gli Elementi",6, "Dal 6° livello, il chierico può usare la reazione per concedere resistenza a danni da acido, freddo, fuoco, fulmine o tuono entro 9m."),
                    Create("Colpo Divino",8, "Dall'8° livello, una volta per turno, il chierico può infliggere 1d8 danni extra (freddo, fuoco o fulmine) con un attacco con arma. 2d8 al 14° livello."),
                    Create("Maestro della Natura",17, "Dal 17° livello, il chierico può o, il chierico ottiene la capacità di comandare gli animali e le creature vegetali."),
                    //Dominio dell'Ordine
                    Create("Incantesimi del Dominio dell'Ordine",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Competenze Bonus",1, "Quando sceglie questo dominio al 1° livello, il chierico acquisisce competenza con armature pesanti. Inoltre, ottiene competenza nell'abilità Intimidire o Persuasione (a scelta)."),
                    Create("Voce dell'Autorità",1, "Al 1° livello, il chierico può invocare il potere della legge per rafforzare un alleato nell'attacco. Se lancia un incantesimo con uno slot di 1° livello o superiore e prende come bersaglio un alleato, quell'alleato può usare la sua reazione immediatamente dopo il lancio dell'incantesimo per effettuare un attacco con arma contro una creatura che il chierico può vedere."),
                    Create("Incanalare Divinità: Pretesa dell'Ordine",2, "Dal 2° livello, il chierico può usare il suo Incanalare Divinità per esercitare una presenza intimidatoria sugli altri. Come azione, il chierico presenta il suo simbolo sacro e ogni creatura di sua scelta che può vederlo o udirlo entro 9 metri deve superare un tiro salvezza su Saggezza, o essere incantata dal chierico fino alla fine del suo prossimo turno o finché non subisce danni. Il chierico può anche far cadere ciò che una creatura sta tenendo se fallisce il tiro salvezza."),
                    Create("Incarnazione della Legge",6, "Dal 6° livello, il chierico diventa particolarmente abile nel canalizzare l'energia magica per costringere gli altri. Se lancia un incantesimo della scuola di incantamento usando uno slot di 1° livello o superiore, può cambiare il tempo di lancio dell'incantesimo in 1 azione bonus per questo lancio, a condizione che il tempo di lancio dell'incantesimo sia normalmente di 1 azione. Può usare questa caratteristica un numero di volte pari al suo modificatore di Saggezza (minimo una volta), e recupera tutte le volte di utilizzo quando termina un riposo lungo."),
                    Create("Colpo Divino",8, "All'8° livello, il chierico ottiene la capacità di infondere i suoi colpi con energia divina. Una volta per turno, quando colpisce una creatura con un attacco con arma, può far sì che l'attacco infligga 1d8 danni psichici extra alla creatura. Quando raggiunge il 14° livello, i danni extra aumentano a 2d8."),
                    Create("Ira dell'Ordine",17, "Dal 17° livello, i nemici designati per la distruzione si piegano sotto gli sforzi combinati del chierico e dei suoi alleati. Se il chierico infligge danni con il suo Colpo Divino a una creatura nel suo turno, può maledire quella creatura fino all'inizio del suo prossimo turno. La prossima volta che un alleato colpisce la creatura maledetta, essa subisce anche 2d8 danni psichici, e la maledizione termina. Il chierico può maledire una creatura in questo modo solo una volta per turno."),
                    //Dominio della Pace
                    Create("Incantesimi del Dominio della Pace",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Strumento di Pace",1, "Quando sceglie questo dominio al 1° livello, il chierico acquisisce competenza nell'abilità Intuizione, Intrattenere o Persuasione (a scelta)."),
                    Create("Legame Incoraggiante", 1,"Al 1° livello, il chierico può forgiare un legame che potenzia tra le creature in pace tra loro. Come azione, sceglie un numero di creature volontarie entro 9 metri pari al suo bonus di competenza. Crea un legame magico tra loro per 10 minuti o fino a quando non usa nuovamente questa caratteristica. Mentre una creatura legata si trova entro 9 metri di un'altra, può tirare un d4 e aggiungere il risultato a un tiro per colpire, una prova di abilità o un tiro salvezza che sta effettuando. Ogni creatura può aggiungere il d4 al massimo una volta per turno."),
                    Create("Incanalare Divinità: Balsamo di Pace",2, "Dal 2° livello, il chierico può usare il suo Incanalare Divinità per rendere la sua presenza un balsamo lenitivo. Come azione, può muoversi fino alla sua velocità senza provocare attacchi di opportunità, e quando si avvicina a meno di 1,5 metri da una creatura durante questa azione, può ristabilire un numero di punti ferita pari a 2d6 + il suo modificatore di Saggezza (minimo 1 punto ferita) alla creatura. Una creatura può ricevere questa guarigione solo una volta ogni volta che il chierico compie questa azione."),
                    Create("Legame Protettivo", 6,"Dal 6° livello, il legame che il chierico crea tra le persone aiuta a proteggerle a vicenda. Quando una creatura influenzata dal Legame Incoraggiante sta per subire danni, una seconda creatura legata entro 9 metri della prima può usare la sua reazione per teletrasportarsi in uno spazio non occupato entro 1,5 metri dalla prima creatura. La seconda creatura subisce tutti i danni al suo posto."),
                    Create("Incantesimi Potenziati",8, "All'8° livello, il chierico aggiunge il suo modificatore di Saggezza ai danni inflitti con qualsiasi cantrip da chierico."),
                    Create("Legame Espansivo",17, "Dal 17° livello, i benefici delle caratteristiche Legame Incoraggiante e Legame Protettivo ora funzionano quando le creature si trovano entro 18 metri l'una dall'altra. Inoltre, quando una creatura usa Legame Protettivo per prendere i danni di un'altra, la creatura ha resistenza a quei danni."),
                    // Dominio della Tempesta
                    Create("Incantesimi del Dominio della Tempesta",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Competenze Bonus",1, "Al 1° livello, il chierico ottiene competenza nelle armi marziali e nelle armature pesanti"),
                    Create("Collera della Tempesta",1, "Al 1° livello, il chierico può punire con tuoni gli attaccanti. Quando una creatura entro 1,5 metri che può vedere lo colpisce con un attacco, può usare la sua reazione per costringere la creatura a effettuare un tiro salvezza su Destrezza. La creatura subisce 2d8 danni da fulmine o tuono (a scelta del chierico) in caso di fallimento, o la metà in caso di successo. Può usare questa caratteristica un numero di volte pari al suo modificatore di Saggezza (minimo una volta). Recupera tutti gli usi con un riposo lungo"),
                    Create("Incanalare Divinità: Collera Distruttiva",2, "Dal 2° livello, il chierico può usare il suo Incanalare Divinità per scatenare la tempesta con ferocia incontrollata. Quando tira danni da fulmine o da tuono, può scegliere di infliggere il massimo danno invece di tirare i dadi"),
                    Create("Colpo del Tuono e del Fulmine",6, "Dal 6° livello, quando il chierico infligge danni da fulmine a una creatura di taglia Grande o inferiore, può anche spingerla fino a 3 metri lontano da sé"),
                    Create("Colpo Divino",8, "All'8° livello, il chierico può infondere i propri attacchi con energia divina. Una volta per turno, quando colpisce una creatura con un attacco con arma, può infliggere 1d8 danni da tuono extra. Al 14° livello, i danni extra aumentano a 2d8"),
                    Create("Nato dalla Tempesta",17, "Dal 17° livello, il chierico ottiene una velocità di volo pari alla sua velocità di camminata, finché non si trova sottoterra o al chiuso"),
                    // Dominio dell'Inganno
                    Create("Incantesimi del Dominio dell'Inganno",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Benedizione dell'Ingannatore",1, "Al 1° livello, il chierico può usare un'azione per toccare una creatura consenziente diversa da sé e conferirle vantaggio alle prove di Destrezza (Furtività). La benedizione dura 1 ora, o fino a quando non viene usata di nuovo questa caratteristica"),
                    Create("Incanalare Divinità: Invocare Duplicato",2, "Dal 2° livello, il chierico può usare il suo Incanalare Divinità per creare un duplicato illusorio di sé stesso. Come azione, crea un'illusione perfetta in uno spazio non occupato che può vedere entro 9 metri. L'illusione dura 1 minuto o finché il chierico mantiene la concentrazione (come se fosse un incantesimo). Come azione bonus nel suo turno, può muovere l'illusione fino a 9 metri in uno spazio visibile, purché resti entro 36 metri da lui. Durante la durata, può lanciare incantesimi come se fosse nello spazio dell'illusione, usando però i propri sensi. Inoltre, se il chierico e l'illusione si trovano entro 1,5 metri da una creatura che può vedere l'illusione, il chierico ha vantaggio ai tiri per colpire contro quella creatura"),
                    Create("Incanalare Divinità: Manto di Ombre",6, "Dal 6° livello, il chierico può usare il suo Incanalare Divinità per svanire. Come azione, diventa invisibile fino alla fine del suo turno successivo. L'invisibilità termina se attacca o lancia un incantesimo"),
                    Create("Colpo Divino",8, "All'8° livello, il chierico può infondere i propri attacchi con veleno. Una volta per turno, quando colpisce una creatura con un attacco con arma, può infliggere 1d8 danni da veleno extra. Al 14° livello, i danni extra aumentano a 2d8"),
                    Create("Duplicato Migliorato", 17,"Dal 17° livello, il chierico può creare fino a quattro duplicati di sé stesso, invece di uno, quando usa Invocare Duplicato. Come azione bonus nel suo turno, può muovere uno qualsiasi dei duplicati fino a 9 metri, entro un raggio massimo di 36 metri"),
                    // Dominio del Crepuscolo
                    Create("Incantesimi del Dominio del Crepuscolo",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Benedizione del Crepuscolo",1, "Al 1° livello, il chierico può usare un'azione per toccare una creatura consenziente diversa da sé e conferirle vantaggio alle prove di Destrezza (Furtività). La benedizione dura 1 ora, o fino a quando non viene usata di nuovo questa caratteristica"),
                    Create("Competenze Bonus",1, "Al 1° livello, il chierico ottiene competenza nelle armi da guerra e nelle armature pesanti"),
                    Create("Occhi della Notte",1, "Dal 1° livello, il chierico ottiene scurovisione con un raggio di 90 metri. In quell'area può vedere nella luce fioca come se fosse luce intensa e nell'oscurità come se fosse luce fioca. Come azione, può condividere magicamente questa scurovisione con un numero di creature consenzienti che può vedere entro 3 metri pari al suo modificatore di Saggezza (minimo una creatura). La visione condivisa dura 1 ora. Una volta usata questa capacità, non può riutilizzarla fino al termine di un riposo lungo, a meno che non spenda uno slot incantesimo di qualsiasi livello"),
                    Create("Benedizione Vigile", 1,"Dal 1° livello, come azione, il chierico può toccare una creatura (incluso sé stesso) per conferirle vantaggio alla prossima prova di iniziativa. Il beneficio termina subito dopo il tiro o se viene usata di nuovo questa caratteristica"),
                    Create("Incanalare Divinità: Santuario del Crepuscolo",2, "Dal 2° livello, il chierico può usare il suo Incanalare Divinità per creare un'aura di crepuscolo. Come azione, presenta il suo simbolo sacro e crea una sfera con raggio di 9 metri centrata su di sé, piena di luce fioca, che si muove con lui e dura 1 minuto (o finché non viene incapacitato o muore). Ogni volta che una creatura (incluso il chierico) termina il proprio turno all'interno della sfera, il chierico può concedere uno di questi benefici: lancia temporanei pari a 1d6 + livello da chierico, oppure rimuovere una condizione di affascinato o spaventato"),
                    Create("Passi della Notte",6, "Dal 6° livello, quando si trova in luce fioca o oscurità, il chierico può usare un'azione bonus per ottenere velocità di volo pari alla propria velocità di camminata per 1 minuto. Può usare questa capacità un numero di volte pari al proprio bonus di competenza, e recupera tutti gli usi al termine di un riposo lungo"),
                    Create("Colpo Divino",8, "All'8° livello, il chierico può infondere i propri attacchi con energia radiante. Una volta per turno, quando colpisce con un attacco con arma, può infliggere 1d8 danni radianti extra. Al 14° livello, i danni extra aumentano a 2d8"),
                    Create("Sudario del Crepuscolo",17, "Dal 17° livello, il chierico e i suoi alleati hanno mezza copertura mentre si trovano all'interno della sfera del Santuario del Crepuscolo"),
                    // Dominio della Guerra
                    Create("Incantesimi del Dominio della Guerra",1, "A partire dal 1° livello, il chierico apprende incantesimi legati al dominio in base al livello di classe. Sono considerati incantesimi da chierico, ma non contano nel numero di incantesimi che può preparare."),
                    Create("Benedizione della Guerra",1, "Al 1° livello, il chierico può usare un'azione per toccare una creatura consenziente diversa da sé e conferirle vantaggio alle prove di Destrezza (Furtività). La benedizione dura 1 ora, o fino a quando non viene usata di nuovo questa caratteristica"),
                    Create("Competenza Bonus",1, "Al 1° livello, il chierico ottiene competenza nelle armi da guerra e nelle armature pesanti"),
                    Create("Sacerdote di Guerra",1, "Dal 1° livello, quando usa l'azione Attacco, il chierico può compiere un attacco con arma come azione bonus. Può usare questa capacità un numero di volte pari al suo modificatore di Saggezza (minimo una volta), e recupera tutti gli usi al termine di un riposo lungo"),
                    Create("Incanalare Divinità: Colpo Guidato", 2,"Dal 2° livello, quando effettua un tiro per colpire, il chierico può usare l'Incanalare Divinità per ottenere un bonus di +10 al tiro. Deve dichiararlo dopo il tiro ma prima di sapere se colpisce o meno"),
                    Create("Incanalare Divinità: Benedizione del Dio della Guerra",6, "Dal 6° livello, quando una creatura entro 9 metri effettua un tiro per colpire, il chierico può usare la reazione per concederle un bonus di +10 al tiro, usando l'Incanalare Divinità. Deve dichiararlo dopo il tiro ma prima di sapere se colpisce o meno"),
                    Create("Colpo Divino",8, "All'8° livello, il chierico può infondere i propri attacchi con energia divina. Una volta per turno, quando colpisce con un attacco con arma, può infliggere 1d8 danni extra dello stesso tipo dell'arma. Al 14° livello, i danni extra aumentano a 2d8"),
                    Create("Avatar della Battaglia",17, "Dal 17° livello, il chierico ottiene resistenza ai danni contundenti, perforanti e taglienti da attacchi non magici"),

                //DRUIDO
                    Create("Incantesimi",1, "Dal 1° livello, il druido può lanciare incantesimi attingendo all'essenza divina della natura stessa. Usa Saggezza come caratteristica da incantatore e prepara un numero di incantesimi ogni giorno pari al suo modificatore di Saggezza + livello da druido"),
                    Create("Druidico",1, "Dal 1° livello, il druido conosce il Druidico, una lingua segreta usata per comunicare e lasciare messaggi nascosti. Solo chi conosce il linguaggio può leggerli senza aiuti magici"),
                    Create("Forma Selvatica", 2,"Dal 2° livello, il druido può usare un'azione per trasformarsi in una bestia che ha già visto. Può farlo due volte, recuperando gli usi con un riposo breve o lungo. Il GS massimo e le capacità della creatura dipendono dal livello. La trasformazione dura un numero di ore pari a metà del livello da druido (arrotondato per difetto)"),
                    Create("Circolo Druidico",2, "Al 2° livello, il druido sceglie un Circolo che gli conferisce privilegi aggiuntivi al 2°, 6°, 10° e 14° livello"),
                    Create("Compagno Selvatico (Opzionale)",2, "Dal 2° livello, il druido può usare un uso di Forma Selvatica per lanciare Trova Famiglio senza componenti materiali. Il famiglio è un folletto e dura un numero di ore pari a metà del livello da druido"),
                    Create("Trucchetti Versatili (Opzionale)",1, "Quando ottiene Aumento dei Punteggi Caratteristica, il druido può sostituire un trucchetto noto con un altro dalla lista dei trucchetti da druido"),
                    Create("Corpo Senza Tempo", 18,"Dal 18° livello, per ogni 10 anni che passano, il corpo del druido invecchia solo di 1 anno"),
                    Create("Incantesimi Bestiali",18, "Dal 18° livello, il druido può lanciare incantesimi anche in forma bestiale, purché non richiedano componenti materiali con costo"),
                    Create("Arcidruido", 20,"Al 20° livello, il druido può usare Forma Selvatica un numero illimitato di volte. Inoltre, può ignorare le componenti verbali, somatiche e materiali senza costo degli incantesimi, anche in forma bestiale"),
                    //Circolo dei Sogni
                    Create("Balsamo della Corte dell'Estate",2, "A partire dal 2° livello, come azione bonus, può curare un alleato entro 36m spendendo un numero di d6 pari a metà del livello da druido o meno. L'alleato recupera tanti PF quanto il totale dei dadi lanciati e ottiene 1 PF temporaneo per ogni dado. I dadi si rigenerano con un riposo lungo"),
                    Create("Focolare della Luce Lunare e dell'Ombra",6, "A partire dal 6° livello, durante un riposo, crea una sfera invisibile di 9m di raggio che fornisce +5 a Furtività e Percezione a chi è dentro. La luce delle fiamme non è visibile dall'esterno"),
                    Create("Sentieri Nascosti", 10,"A partire dal 10° livello, come azione bonus, può teletrasportarsi di 18m. Oppure, come azione, può teletrasportare un alleato toccato di 9m. Usabile un numero di volte pari al modificatore di Saggezza (minimo 1), si ricarica dopo un riposo lungo"),
                    Create("Viandante dei Sogni",14, "A partire dal 14° livello, dopo un riposo breve, può lanciare Sogno, Scrutare o Cerchio di Teletrasporto (verso l'ultimo luogo dove ha fatto un riposo lungo). Una volta usato, si ricarica dopo un riposo lungo"),
                    //Circolo della Terra
                    Create("Trucchetto Bonus",2, "Quando un druido sceglie questo Circolo al 2° livello, impara un trucchetto aggiuntivo a sua scelta dalla lista dei trucchetti del druido. Questo trucchetto non conta contro il numero di cantrip che il druido conosce."),
                    Create("Recupero Naturale",2, "A partire dal 2° livello, durante un riposo breve, il druido può recuperare parte della sua energia magica meditandosi e comunando con la natura. Il druido sceglie un numero di slot di incantesimi spesi da recuperare, la cui somma dei livelli non superi la metà del suo livello da druido (arrotondato per eccesso). Il druido non può recuperare slot di incantesimi di 6° livello o superiore e non può usare questa caratteristica nuovamente finché non completa un riposo lungo."),
                    Create("Incantesimi del Circolo", 3,"Quando il druido raggiunge il 3°, 5°, 7° e 9° livello, la sua connessione mistica con la terra gli permette di lanciare incantesimi speciali. Ogni volta che sale di livello, il druido ottiene l'accesso a un gruppo di incantesimi del Circolo legati alla terra dalla quale ha ottenuto il suo potere druidico. Il druido sceglie il tipo di terra (artico, costa, deserto, foresta, prateria, montagna, palude o sottosuolo) e consulta la lista di incantesimi associata. Una volta che un incantesimo del Circolo è acquisito, il druido lo ha sempre preparato e non conta al fine di determinare il numero di incantesimi che può preparare ogni giorno."),
                    Create("Andatura sul Territorio",6, "A partire dal 6° livello, il druido può muoversi attraverso terreni difficili non magici senza che ciò gli costi movimento extra. Inoltre, il druido può passare attraverso vegetali non magici senza essere rallentato da esse e senza subire danni da vegetali che hanno spine, aculei o pericoli simili. Il druido ha anche vantaggio nei tiri salvezza contro vegetali creati o manipolati magicamente per ostacolare il movimento."),
                    Create("Interdizione della Natura",10, "Al 10° livello, il druido non può essere incantato o spaventato da creature elementali o fatate, e diventa immune ai veleni e alle malattie."),
                    Create("Rifugio della Natura",14, "A partire dal 14° livello, le creature del mondo naturale percepiscono la connessione del druido con la natura e diventano esitanti nell'attaccarlo. Quando una creatura bestia o pianta attacca il druido, essa deve eseguire un tiro salvezza di Saggezza contro il CD di salvezza degli incantesimi del druido. Se il tiro salvezza fallisce, la creatura deve scegliere un altro bersaglio o l'attacco fallisce automaticamente. Se il tiro salvezza ha successo, la creatura è immune a questo effetto per 24 ore. La creatura è consapevole di questo effetto prima di fare l'attacco contro il druido."),
                    //Circolo della Luna
                    Create("Forma Selvatica da Combattimento",2, "Quando il druido sceglie questo Circolo al 2° livello, ottiene la capacità di usare la Forma Selvatica durante il suo turno come azione bonus, anziché come azione principale. Inoltre, mentre è trasformato tramite la Forma Selvatica, il druido può usare un'azione bonus per spendere uno slot incantesimo e recuperare 1d8 punti ferita per ogni livello dello slot incantesimo speso."),
                    Create("Forme del Circolo", 2,"I rituali del Circolo conferiscono al druido la capacità di trasformarsi in forme animali più potenti. A partire dal 2° livello, il druido può usare la Forma Selvatica per trasformarsi in una bestia con un punteggio di sfida fino a 1. Il druido ignora la colonna Punteggio di Sfida Max. della tabella delle Forme delle Bestie, ma deve rispettare tutte le altre limitazioni indicate nella tabella."),
                    Create("Colpo Primordiale",6, "A partire dal 6° livello, gli attacchi del druido in forma bestiale sono considerati magici ai fini di oltrepassare le resistenze e immunità agli attacchi e danni non magici."),
                    Create("Forma Selvatica Elementale",10, "Al 10° livello, il druido può spendere due usi della Forma Selvatica contemporaneamente per trasformarsi in un elementale d'aria, un elementale di terra, un elementale di fuoco o un elementale d'acqua."),
                    Create("Mille Forme",10, "Al 14° livello, il druido ha imparato a usare la magia per alterare la sua forma fisica in modi più sottili. Il druido può lanciare l'incantesimo Alterare se stesso a volontà."),
                    //Circolo del Pastore
                    Create("Lingua dei Boschi",2, "Dal 2° livello il druido parla, legge e scrive il Silvano. Le bestie comprendono il suo linguaggio e lui può interpretarne versi e gesti."),
                    Create("Totem Spirituale", 2,"Dal 2° livello il druido evoca uno spirito animale come azione bonus entro 18 m. L'aura è di 9 m e dura 1 min. (per maggiori dettagli consultare il manuale: XGE)"),
                    Create("Evocatore Possente",6, "Dal 6° livello le bestie e i folletti evocati dal druido ottengono 2 PF extra per dado vita e i loro attacchi naturali contano come magici contro resistenze e immunità."),
                    Create("Spirito Guardiano",10, "Dal 10° livello le creature evocate dal druido recuperano PF pari a metà del suo livello se terminano il turno nell'aura dello Spirito Totem."),
                    Create("Evocazioni Fedeli",14, "Dal 14° livello, se il druido è a 0 PF o incapacitato, evoca 4 bestie di GS 2 entro 6 m come con Evoca Animali al 9°. Durano 1 ora, lo proteggono e non richiedono concentrazione."),
                    //Circolo delle Spore
                    Create("Incantesimi del Circolo",2, "Dal 2° livello, il druido acquisisce un legame simbiotico con i funghi e la morte. Impara il trucchetto Toccare Gelido."),
                    Create("Alone di Spore",2, "Dal 2° livello il druido è circondato da spore necrotiche invisibili. Quando una creatura si avvicina a 3 m o inizia il suo turno lì, usa la reazione per infliggere danno necrotico. Danno aumenta con il livello."),
                    Create("Entità Simbiotica",2, "Dal 2° livello il druido canalizza magia nelle sue spore. Usando la sua Forma Selvatica, guadagna HP temporanei e ottiene benefici: doppio danno per Alone di Spore e +1d6 danno necrotico ai suoi attacchi."),
                    Create("Infestazione Fungina",6, "Dal 6° livello, il druido può infestare un corpo morto e animarlo come uno zombie per 1 ora. Obbedisce ai suoi comandi e può attaccare. Può farlo un numero di volte pari al suo modificatore di Saggezza."),
                    Create("Diffusione di Spore",10, "Dal 10° livello il druido può seminare un'area con spore mortali come azione bonus. Ogni creatura che entra o inizia il turno nell'area subisce danni da Alone di Spore. L'area dura 1 min o finché non viene rimossa."),
                    Create("Corpo Fungino",14, "Dal 14° livello il druido diventa immune a cecità, sordità, paura e veleno. Ogni colpo critico contro di lui diventa normale, a meno che non sia incapacitato."),
                    //Circolo delle Stelle
                    Create("Carta Celeste",2, "Al 2° livello il druido crea una mappa stellare, usabile come focus. Impara il trucchetto Guida e ha Dardo Tracciato sempre preparato."),
                    Create("Forma Siderale", 2,"Al 2° livello usando Forma Selvatica, assume una forma luminosa per 10 min. Può scegliere tra tre costellazioni che forniscono bonus (per maggiori dettagli consultare il manuale: TCOE)."),
                    Create("Profezia Cosmica",6, "Al 6° livello Al termine di un riposo lungo, il druido pud consultare la sua Carta Celeste per carpire presagi. Quando lo fa, deve tirare un dado (per maggiori dettagli consultare il manuale: TCOE)."),
                    Create("Costellazioni Scintillanti",10, "Al 10° livello le costellazioni generate con il privilegio Forma Siderale vengono migliorate. "),
                    Create("Manto di Stelle",14, "Al 14° livello il druido in Forma Siderale ottiene resistenza a danni contundenti, perforanti e taglienti."),
                    //Circolo della Fiamma
                    Create("Incantesimi del Circolo",2, "Quando si unisce al circolo, stringe un patto col fuoco primordiale. Ottiene incantesimi bonus sempre preparati al 3°, 5°, 7° e 9° livello, che diventano parte della lista del druido (per maggiori dettagli consultare il manuale: TCOE)."),
                    Create("Evocare Spirito della Fiamma",2, "Al 2° livello usando Forma Selvatica, il druido evoca uno spirito in uno spazio entro 9m. Lo spirito agisce dopo di te e dura 1 ora."),
                    Create("Legame Migliorato",6, "A partire dal 6° livello, il legame con lo Spirito della Fiamma aumenta i poteri degli incantesimi distruttivi e ristorativi del druido."),
                    Create("Fiamme Cauterizzanti",10, "Al 10° livello il druido ottiene la capacita di trasformare la morte in fiamme magiche che possono curare o incenerire."),
                    Create("Recupero Ardente",14, "Al 14° livello il legame con lo Spirito della Fiamma pud salvare il druido dalla morte. Se i punti ferita del druido scendono a O facendolo cadere privo di sensi e lo spirito si trova entro un raggio di 36 metri da lui, il druido pud fare in modo che i punti ferita dello spirito scendano anch'essi a 0 per poter recuperare la meta dei suoi punti ferita e rialzarsi immediatamente in piedi. "),

                //GUERRIERO
                    Create("Stile di Combattimento",1, "Al 1° livello, il guerriero adotta uno stile di combattimento, scegliendolo tra varie specializzazioni disponibili sul manuale PHB"),
                    Create("Recupero Energie",1, "Fin dal 1° livello, può usare un'azione bonus per recuperare 1d10 + livello da guerriero. Può farlo una volta per riposo breve o lungo."),
                    Create("Azione Impetuosa",2, "Dal 2° livello, il guerriero può superare i propri limiti e compiere un'azione extra nel turno. Usabile una volta per riposo; due volte dal 17°, ma non nello stesso turno."),
                    Create("Archetipo Marziale",3, "Dal 3° livello, sceglie un archetipo da seguire. Ottiene privilegi aggiuntivi al 3°, 7°, 10°, 15° e 18° livello."),
                    Create("Versatilità Marziale (Opzionale)",1, "Ogni volta che ottiene un incremento di caratteristica, può cambiare stile di combattimento o una manovra se appartiene al Maestro di Battaglia."),
                    //---->Attacco Extra vedi sopra
                    Create("Indomito",9, "Dal 9° livello, può ritirare un tiro salvezza fallito una volta per riposo lungo. Due usi dal 13°, tre dal 17° livello."),

                    //Campione
                    Create("Critico Migliorato",3, "Dal 3° livello, infligge colpi critici con un 19 o 20 al tiro per colpire."),
                    Create("Atleta Straordinario",7, "Dal 7° livello, aggiunge metà del bonus competenza (arrotondato per eccesso) a prove di Forza, Destrezza o Costituzione non già competenti. Salti in lungo migliorati di un numero di piedi pari al modificatore di Forza."),
                    Create("Stile di Combattimento Extra",10, "Dal 10° livello, può scegliere un secondo stile di combattimento."),
                    Create("Critico Superiore",15, "Dal 15° livello, infligge colpi critici con un 18, 19 o 20."),
                    Create("Sopravvissuto",18, "Dal 18° livello, se ha metà dei PF o meno e non è a 0, recupera 5 + mod. Costituzione all'inizio di ogni turno."),
                    //Maestro di Battaglia
                    Create("Superiorità in Combattimento",3, "Dal 3° livello, ottiene manovre speciali alimentate da dadi di superiorità."),
                    Create("Studioso di Guerra",3, "Dal 3° livello, ottiene competenza in un tipo di strumenti da artigiano a scelta."),
                    Create("Conosci il Tuo Nemico",7, "Dal 7° livello, può confrontare due caratteristiche di una creatura osservata per 1 min: se è superiore, pari o inferiore."),
                    Create("Superiorità di Combattimento Migliorata",10, "Dal 10° livello i dadi di superiorità diventano d10, al 18° diventano d12."),
                    Create("Implacabile",15, "Dal 15° livello, se inizia il combattimento senza dadi di superiorità, ne recupera 1."),
                    //Cavaliere Mistico
                    Create("Incantesimi",3, "Dal 3° livello, combina potere marziale e magia, ottenendo la capacità di lanciare incantesimi."),
                    Create("Arma Vincolata",3, "Dal 3° livello, lega magicamente un'arma: non può essere disarmato e può richiamarla con un'azione bonus."),
                    Create("Magia da Guerra",7, "Dal 7° livello, se lancia un trucchetto con l'azione, può attaccare con un'arma come azione bonus."),
                    Create("Colpo Mistico",10, "Dal 10° livello, un colpo riduce la resistenza ai suoi incantesimi: il bersaglio ha svantaggio al prossimo tiro salvezza."),
                    Create("Carica Arcana",15, "Dal 15° livello, quando usa Impeto d'Azione, può teletrasportarsi di 9 metri prima o dopo l'azione extra."),
                    Create("Magia da Guerra Migliorata", 18,"Dal 18° livello, se lancia un incantesimo con l'azione, può attaccare con un'arma come azione bonus."),
                    //Arciere Arcano
                    Create("Conoscenze Dell'Arciere Arcano",3, "Dal 3° livello, ottiene un trucchetto (Prestidigitazione o Artificio Druidico) e competenza in Arcano o Natura."),
                    Create("Tiro Arcano",3, "Dal 3° livello, imbeve frecce con effetti magici scelti. Ne può usare 2 per riposo e ne apprende altri ai livelli 7, 10, 15 e 18."),
                    Create("Freccia Magica",7, "Dal 7° livello, ogni freccia non magica lanciata con arco corto o lungo diventa magica contro resistenze."),
                    Create("Tiro Curvo",7, "Dal 7° livello, se manca un bersaglio con una freccia magica, può ritentare su un altro entro 18 metri."),
                    Create("Tiro Sempre Pronto",15, "Dal 15° livello, se inizia il combattimento senza usi di Tiro Arcano, ne recupera uno."),
                    //Cavaliere Errante
                    Create("Competenza Bonus",3, "Dal 3° livello, ottiene competenza in un'abilità (Addestrare Animali, Storia, Intuizione, Intrattenere o Persuasione) oppure una lingua a scelta."),
                    Create("Nato in Sella",3, "Dal 3° livello, ha vantaggio per non cadere da cavallo e atterra in piedi se cade da meno di 3 metri. Montare/smontare costa solo 1,5 metri di movimento."),
                    Create("Marchio Incrollabile",3, "Dal 3° livello, può marchiare un nemico colpito in mischia, dandogli svantaggio se attacca altri. Se ferisce un alleato, può attaccarlo con vantaggio infliggendo danni extra. Usabile un numero di volte pari al modificatore di Forza (minimo 1) per riposo lungo."),
                    Create("Manovra Protettrice",7, "Dal 7° livello, può usare la reazione per aumentare la CA di un alleato entro 1,5 metri con 1d8, ottenendo anche resistenza se l'attacco colpisce. Usabile tante volte quanto il modificatore di Costituzione (minimo 1) per riposo lungo."),
                    Create("Mantenere la Posizione",10, "Dal 10° livello, può effettuare attacchi di opportunità su nemici che si muovono di almeno 1,5 metri nel suo raggio. Se colpisce, il bersaglio non può più muoversi fino alla fine del turno."),
                    Create("Carica Feroce",15, "Dal 15° livello, dopo una carica di almeno 3 metri in linea retta, se colpisce un nemico, questo deve superare un tiro salvezza su Forza o cade prono. Usabile una volta per turno."),
                    Create("Difensore Vigile",18, "Dal 18° livello, ottiene una reazione speciale da usare su ogni turno degli altri, solo per effettuare attacchi di opportunità. Non può usarla nel proprio turno."),
                    //Guerriero Psionico
                    Create("Potere Psionico",3, "Dal 3° livello, ottiene un numero di Dadi di Energia Psionica pari al doppio del bonus di competenza (d6 → d8 al 5°, d10 all'11°, d12 al 17°). Si usano per attivare poteri psionici e si recuperano con un riposo lungo. Una volta per riposo breve/lungo può recuperarne uno come azione bonus."),
                    Create("Adepto Telecinetico",7, "Dal 7° livello, può usare due nuove abilità:Salto Psionico: come bonus action, ottiene volo pari al doppio della velocità fino a fine turno (una volta per riposo o spendendo un dado psionico). Spinta Telecinetica: quando usa Colpo Psionico, può far fare un TS su Forza (CD = 8 + competenza + Int) al bersaglio. Se fallisce, lo spinge di 3 metri o lo fa cadere prono."),
                    Create("Scudo Mentale",10, "Dal 10° livello, ottiene resistenza ai danni psichici. Se è affascinato o spaventato all'inizio del suo turno, può spendere un dado psionico per terminare l'effetto."),
                    Create("Baluardo della Forza",15, "Dal 15° livello, come azione bonus, può garantire mezza copertura per 1 minuto a un numero di creature (entro 9 metri) pari al modificatore Intelligenza (minimo 1). Usabile una volta per riposo lungo o spendendo un dado psionico."),
                    Create("Maestro della Telecinesi",18, "Dal 18° livello, può lanciare Telecinesi senza componenti (CD basata su Intelligenza). Durante la concentrazione, può effettuare un attacco con arma come azione bonus. Usabile una volta per riposo lungo o spendendo un dado psionico."),
                    //Guerriero Runico
                    Create("Competenze Bonus",3, "Dal 3° livello, ottiene competenza negli strumenti da fabbro e conosce la lingua dei Giganti."),
                    Create("Intagliatore di Rune",3, "Dal 3° livello, impara 2 rune magiche che può incidere sull'equipaggiamento per attivarle. Ottiene rune aggiuntive ai livelli successivi e può sostituirne una ogni volta che sale di livello. Le rune conosciute si attivano una volta per riposo lungo (o due dal 15° livello)."),
                    Create("Potenza del Gigante",3, "Dal 3° livello, come azione bonus può trasformarsi per 1 minuto: Diventa di Taglia Grande (se possibile); Vantaggio a prove e TS di Forza; Una volta per turno infligge +1d6 danni extra (1d8 al 10°, 1d10 al 18°)."),
                    Create("Scudo Runico",7, "Dal 7° livello, quando una creatura entro 18 metri viene colpita, può usare la reazione per far ritirare il d20 d'attacco all'assalitore (deve tenere il nuovo risultato). Usi pari al bonus di competenza per riposo lungo."),
                    Create("Statura Torreggiante",10, "Dal 10° livello, cresce permanentemente di 3d4 centimetri. Il danno bonus di Potenza del Gigante aumenta a 1d8."),
                    Create("Maestro delle Rune",15, "Dal 15° livello, può attivare ogni runa conosciuta due volte per riposo breve o lungo."),
                    Create("Colosso Runico", 18,"Dal 18° livello, durante Potenza del Gigante può diventare Huge e ottiene +1,5m di portata. Il danno bonus sale a 1d10."),
                    //Samurai
                    Create("Competenza Bonus",3, "A partire dal 3° livello, ottieni competenza in una abilità a scelta tra Storia, Intuizione, Intrattenere o Persuasione. In alternativa, impari una lingua a scelta."),
                    Create("Spirito Comb attivo",3, "Dal 3° livello, puoi usare un'azione bonus per ottenere vantaggio su tutti gli attacchi fino alla fine del turno e 5 PF temporanei (10 al 10° livello, 15 al 15°). Usi massimi: 3, recuperati dopo un riposo lungo."),
                    Create("Cortigiano Elegante",7, "Dal 7° livello, aggiungi il modificatore di Saggezza ai tiri di Carisma (Persuasione). Ottieni competenza nei tiri salvezza su Saggezza (o Intelligenza/Carisma se già competente)."),
                    Create("Spirito Instancabile",10, "Dal 10° livello, quando tiri iniziativa senza usi di Spirito Combattivo, ne recuperi uno."),
                    Create("Colpo Rapido",15, "Dal 15° livello, se hai vantaggio su un attacco, puoi rinunciarvi per un attacco extra contro lo stesso bersaglio, una volta per turno."),
                    Create("Forza di fronte alla Morte",18, "Dal 18° livello, se ridotto a 0 PF, puoi usare una reazione per fare un turno extra prima di cadere incosciente. Recuperi l'uso dopo un riposo lungo."),  
                    
                //MONACO
                    Create("Difesa Senza Armatura",1, "A partire dal 1° livello, finché non indossa armature né impugna uno scudo, la sua Classe Armatura è pari a 10 + il modificatore di Destrezza + il modificatore di Saggezza."),
                    Create("Arti Marziali",1, "Dal 1° livello, le sue tecniche di combattimento gli permettono di padroneggiare stili basati su colpi senz'armi e armi da monaco (spade corte e armi semplici da mischia prive delle proprietà 'a due mani' o 'pesante'). Può usare la Destrezza al posto della Forza per tiri per colpire e danni. Può tirare 1d4 al posto del normale danno degli attacchi senz'armi o con armi da monaco (il dado aumenta con i livelli da Monaco). Quando usa l'azione Attacco con un colpo senz'armi o un'arma da monaco, può effettuare un altro colpo senz'armi come azione bonus."),
                    Create("Ki",2, "Dal 2° livello, impara a canalizzare l'energia mistica detta 'ki'. Possiede un numero di punti ki pari al suo livello da Monaco, usabili per attivare tecniche speciali. Recupera tutti i punti ki spesi dopo un riposo breve o lungo, meditando per almeno 30 minuti. (per maggiori dettagli consultare il manuale: PHB)"),
                    Create("Movimento Senza Armatura",2, "Dal 2° livello, finché non indossa armature né scudi, la sua velocità aumenta di 3 metri. L'aumento cresce con i livelli. Dal 9° livello, può camminare su superfici verticali e liquidi durante il suo turno senza cadere."),
                    Create("Arma Dedicata (Opzionale)",2, "Dal 2° livello, può scegliere un'arma semplice o marziale (senza le proprietà 'pesante' o 'speciale') alla fine di un riposo e considerarla arma da monaco fino al prossimo uso di questa capacità."),
                    Create("Tradizione Monastica",3, "Dal 3° livello, sceglie una Tradizione Monastica che gli conferisce privilegi aggiuntivi al 3°, 6°, 11° e 17° livello."),
                    Create("Deviare Proiettili",3, "Dal 3° livello, può usare la reazione per deviare o afferrare un'arma a distanza. Riduce il danno di 1d10 + modificatore di Destrezza + livello da Monaco. Se riduce il danno a 0, può afferrare il proiettile (se ha almeno una mano libera) e, spendendo 1 ki, lanciarlo come attacco a distanza (20/60), considerandolo un'arma da monaco."),
                    Create("Attacco Infuso di Ki (Opzionale)",3, "Dal 3° livello, se spende 1 punto ki o più durante il turno, può effettuare un attacco con un colpo senz'armi o arma da monaco come azione bonus."),
                    Create("Caduta Lenta",4, "Dal 4° livello, può usare la reazione per ridurre i danni da caduta di un ammontare pari a cinque volte il suo livello da Monaco."),
                    Create("Guarigione Accelerata (Opzionale)",4, "Dal 4° livello, può usare un'azione per spendere 2 punti ki e tirare un dado delle Arti Marziali. Recupera punti ferita pari al risultato + bonus competenza."),
                    //-----> attacco extra
                    Create("Colpo Stordente",5, "Dal 5° livello, quando colpisce una creatura con un attacco in mischia, può spendere 1 ki per tentare di stordirla. La creatura deve superare un tiro salvezza di Costituzione o resta stordita fino alla fine del suo prossimo turno."),
                    Create("Mira Focalizzata (Opzionale))",5, "Dal 5° livello, quando manca un attacco, può spendere da 1 a 3 punti ki per aumentare il tiro per colpire di 2 per ogni punto speso."),
                    Create("Colpi di Ki Potenziati", 6,"Dal 6° livello, i suoi colpi senz'armi contano come magici per superare resistenze o immunità ai danni non magici."),
                    Create("Elusione", 7,"Dal 7° livello, quando è soggetto a un effetto che richiede un tiro salvezza su Destrezza per dimezzare i danni, non subisce danni se lo supera e solo metà se fallisce."),
                    Create("Mente Lucida", 7,"Dal 7° livello, può usare un'azione per terminare un effetto che lo sta incantando o spaventando."),
                    Create("Purezza del Corpo", 10,"Dal 10° livello, è immune a veleni e malattie grazie al controllo del proprio ki."),
                    Create("Lingua del Sole e della Luna",13, "Dal 13° livello, riesce a comunicare con ogni creatura parlante e comprendere qualsiasi lingua parlata."),
                    Create("Anima Adamantina", 14,"Dal 14° livello, ottiene competenza in tutti i tiri salvezza. Inoltre, quando fallisce un TS, può spendere 1 ki per ritirarlo e usare il secondo risultato."),
                    Create("Corpo Senza Tempo",15, "Dal 15° livello, non invecchia più e non può essere invecchiato magicamente. Non necessita di cibo o acqua."),
                    Create("Corpo Vuoto", 18,"Dal 18° livello, può usare un'azione e spendere 4 ki per diventare invisibile per 1 minuto e ottenere resistenza a tutti i danni tranne quelli da forza. Inoltre, può spendere 8 ki per lanciare *Proiezione Astrale* su se stesso senza materiali, ma non può portare altre creature."),
                    Create("Perfezione Interiore",20, "Dal 20° livello, se tira l'iniziativa con 0 punti ki, ne recupera automaticamente 4."),
                    //Via del sè astrale
                    Create("Braccia del Sé Astrale", 3,"Dal 3° livello, può evocare le braccia del proprio sé astrale usando un'azione bonus e 1 punto ki. Le creature scelte entro 3 metri devono superare un tiro salvezza su Destrezza o subiscono danni da forza pari a due dadi di Arti Marziali. Le braccia restano attive per 10 minuti e forniscono i seguenti benefici: può usare Saggezza al posto di Forza per prove e tiri salvezza di Forza; può effettuare colpi senz'armi con le braccia con gittata aumentata di 1,5 metri; può usare Saggezza al posto di Forza o Destrezza per colpire e infliggere danni; il danno è da forza."),
                    Create("Volto del Sé Astrale",6, "Dal 6° livello, può evocare il volto del sé astrale come azione bonus (anche assieme alle braccia) spendendo 1 punto ki. Dura 10 minuti e conferisce: vista nel buio (magico e non) fino a 36 metri; vantaggio alle prove di Intuizione e Intimidire; può parlare solo a una creatura entro 18 metri o amplificare la voce fino a 180 metri."),
                    Create("Corpo del Sé Astrale",11, "Dall'11° livello, quando ha sia le braccia che il volto evocati, può manifestare anche il corpo del sé astrale (senza usare azioni). Conferisce: può usare la reazione per ridurre danni da acido, freddo, fuoco, forza, fulmine o tuono di 1d10 + modificatore di Saggezza (minimo 1); una volta per turno, se colpisce con le braccia astrali, infligge danni extra pari al dado di Arti Marziali."),
                    Create("Sé Astrale Risvegliato",17, "Dal 17° livello, può evocare tutto il sé astrale (braccia, volto e corpo) usando un'azione bonus e 5 punti ki. Dura 10 minuti. Mentre è attivo: ottiene +2 alla CA; quando usa Attacco Extra, può effettuare tre attacchi con le braccia astrali invece di due."),
                    //Via del maestro ubriaco
                    Create("Competenze Bonus",3, "Dal 3° livello, ottiene competenza in Intrattenere e negli strumenti da birraio, se non le possiede già."),
                    Create("Tecnica dell'Ubriaco",3, "Dal 3° livello, quando usa Raffica di Colpi ottiene automaticamente l'effetto di Disimpegno e la sua velocità aumenta di 3 metri fino alla fine del turno."),
                    Create("Andatura Ondeggiante",6, "Dal 6° livello, ottiene: Balzare in Piedi, che gli permette di rialzarsi da prono usando solo 1,5 metri di movimento; e Ridigere Attacco, che gli consente, quando una creatura lo manca con un attacco in mischia, di spendere 1 punto ki per far colpire una creatura a sua scelta entro 1,5m dall'attaccante."),
                    Create("Fortuna dell'Ubriacone",11, "Dall'11° livello, quando ha svantaggio su un tiro per colpire, una prova o un tiro salvezza, può spendere 2 punti ki per annullare lo svantaggio."),
                    Create("Frenesia Intossicata",17, "Dal 17° livello, quando usa Raffica di Colpi può effettuare fino a 5 attacchi extra, anziché 2, a patto che ognuno bersagli una creatura diversa in quel turno."),
                    //via dei 4 elementi
                    Create("Discepolo degli Elementi",3, "Dal 3° livello, apprende Affinità Elementale e un'altra disciplina elementale a scelta. Ottiene un'altra disciplina al 6°, 11° e 17° livello. Quando ne apprende una nuova, può sostituirne una conosciuta (per maggiori dettagli consultare il manuale: PHB)."),
                    Create("Discipline Elementali",5, "Può lanciare incantesimi delle discipline senza componenti materiali. Dal 5° livello, può spendere ki aggiuntivo per lanciarli a livelli superiori (per maggiori dettagli consultare il manuale: PHB)."),
                    //Via del Kensei
                    Create("Cammino del Kensei",3, "Al 3° livello, padroneggi due armi (1 mischia, 1 a distanza) che diventano armi da monaco. Bonus: +2 CA se colpisci a mani nude, +1d4 danni con armi a distanza, e competenza in strumenti da calligrafo/pittore."),
                    Create("Tutt'uno con la Lama",6, "Al 6° livello, le tue armi kensei superano resistenze magiche. Spendendo 1 punto ki, infliggi danni extra pari al tuo dado Arti Marziali (1 volta/turno)."),
                    Create("Affilare la Lama",11, "All'11° livello, spendi fino a 3 punti ki (azione bonus) per dare bonus a tiri per colpire e danni con un'arma kensei per 1 minuto (bonus = punti ki spesi)."),
                    Create("Precisione Infallibile",17, "Al 17° livello, puoi ritirare un tiro d'attacco fallito con un'arma da monaco, 1 volta per turno."),
                    //Via della Misericordia
                    Create("Strumenti di Misericordia", 3,"Al 3° livello ottieni competenza in Intuizione, Medicina e kit da erbalista. Ricevi una maschera personalizzata che indossi quando usi le abilità di questa tradizione."),
                    Create("Mano Guaritrice",3, "Al 3° livello, spendi 1 punto ki (azione) per curare un bersaglio (dado Arti Marziali + mod. Saggezza)."),
                    Create("Mano del Dolore",3, "Al 3° livello, quando colpisci con un attacco a mani nude, spendi 1 punto ki per infliggere danno necrotico extra (dado Arti Marziali + mod. Saggezza). Utilizzabile 1 volta/turno."),
                    Create("Tocco del Medico",6, "Dal 6° livello, Mano Guaritrice cura anche 1 malattia o condizione (accecato, sordo, paralizzato, avvelenato o stordito). Mano del Dolore infligge anche condizione avvelenato fino al tuo prossimo turno."),
                    Create("Raffica di Guarigione e Dolore", 11,"Dall'11° livello, puoi sostituire tutti gli attacchi di Raffica con Mano Guaritrice senza costo ki. Inoltre, puoi usare Mano del Dolore in Raffica senza spendere ki (sempre 1 volta/turno)."),
                    Create("Mano della Misericordia Suprema",17, "Al 17° livello, spendi 5 punti ki (azione) per resuscitare una creatura morta entro 24 ore (4d10 + mod. Saggezza PF), rimuovendo condizioni debilitanti. Usabile 1 volta per riposo lungo."),
                    //Via della Mano Aperta
                    Create("Tecnica della Mano Aperta", 3,"Dal 3° livello, quando colpisci con Raffica di Colpi puoi: 1) Far cadere a terra (salvataggio Destrezza), 2) Spingere 4.5m (salvataggio Forza), o 3) Togliere reazioni al bersaglio fino al tuo prossimo turno."),
                    Create("Integrità del Corpo", 6,"Al 6° livello, come azione puoi rigenerare PF pari a tre volte il tuo livello da monaco. Utilizzabile 1 volta per riposo lungo."),
                    Create("Tranquillità",11, "Dall'11° livello, dopo un riposo lungo ottieni l'effetto di Santuario (CD 8 + mod. Saggezza + bonus competenza) che dura fino al prossimo riposo lungo."),
                    Create("Palmo Tremante",17, "Al 17° livello, spendi 3 punti ki per impiantare vibrazioni letali con un attacco. Come azione, attivi le vibrazioni: il bersaglio muore o subisce 10d10 danni necrotici."),
                    //Via dell'Ombra
                    Create("Arti delle Ombre",3, "Dal 3° livello, spendi 2 punti ki per lanciare oscurità, visione nel buio, passare senza tracce o silenzio (senza componenti). Ottieni anche il trucchetto Illusione Minore."),
                    Create("Passo dell'Ombra",6, "Al 6° livello, teletrasportati 18m tra zone d'ombra (azione bonus). Ottieni vantaggio sul primo attacco in mischia quel turno."),
                    Create("Manto di Ombre",11, "Dall'11° livello, diventi invisibile in aree poco illuminate (fino ad attaccare, lanciare magie o entrare in luce intensa)."),
                    Create("Opportunista", 17,"Al 17° livello, quando una creatura vicina viene colpita, puoi usare una reazione per attaccarla immediatamente."),
                    //Via dell'Anima Solare
                    Create("Dardo del Sole Radioso",3, "Dal 3° livello, puoi scagliare dardi di luce (attacco a distanza 9m, 1d4 danni radianti). Spendendo 1 punto ki, puoi attaccare due volte come azione bonus. Il dado aumenta con i livelli da monaco."),
                    Create("Colpo dell'Onda Incandescente",6, "Al 6° livello, spendi 2+ punti ki come azione bonus per lanciare Mani Brucianti dopo un attacco. Ogni punto ki extra aumenta di 1 livello l'incantesimo (max metà del tuo livello da monaco)."),
                    Create("Esplosione Solare Incandescente",11, "All'11° livello, crei un'orba esplosiva (45m, 6m raggio, 2d6 danni radianti). Spendendo fino a 3 punti ki, aumenti i danni di 2d6 per punto."),
                    Create("Scudo Solare",17, "Al 17° livello, emani luce (9m luce intensa + 9m luce fioca). Se colpito in mischia, puoi reagire infliggendo 5 + mod. Saggezza danni radianti."),

                //MAGO
                    Create("Incantesimi",1, "Dal 1° livello, il mago possiede un libro degli incantesimi con cui prepara e lancia magie arcane attingendo al proprio intelletto."),
                    Create("Recupero Arcano",1, "Dal 1° livello, durante un riposo breve, può recuperare slot incantesimo per un ammontare pari a metà del livello da mago (arrotondato per eccesso)."),
                    Create("Tradizione Arcana",2, "Dal 2° livello, sceglie una scuola di magia che ne influenza lo stile. Conferisce privilegi extra ai livelli 2, 6, 10 e 14."),
                    Create("Formule dei Trucchetti",3, "Dal 3° livello, ogni riposo lungo può sostituire un trucchetto conosciuto con un altro dal libro, grazie a formule apprese."),
                    Create("Maestria negli Incantesimi",18, "Dal 18° livello, può lanciare a volontà un incantesimo di 1° livello e uno di 2° livello preparati, senza consumare slot."),
                    Create("Incantesimi Personali",20, "Dal 20° livello, sceglie due incantesimi di 3° livello come personali: li ha sempre preparati e può lanciarli una volta senza consumare slot."),
                    //Scuola di Abiurazione
                    Create("Abiuratore Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Abiurazione nel proprio libro."),
                    Create("Interdizione Arcana", 2,"Dal 2° livello, dopo aver lanciato un incantesimo di abiurazione, crea una barriera con PF pari a 2x livello da mago + Int. Assorbe danni finché non crolla. Si rigenera lanciando incantesimi."),
                    Create("Interdizione Proiettata", 6,"Dal 6° livello, può usare la barriera per assorbire danni al posto di una creatura entro 9 metri, usando la reazione."),
                    Create("Abiurazione Migliorata",10, "Dal 10° livello, aggiunge il bonus di competenza alle prove richieste da incantesimi di abiurazione come Dissolvi Magia o Controincantesimo."),
                    Create("Resistenza agli Incantesimi",14, "Dal 14° livello, ha vantaggio ai tiri salvezza contro incantesimi e resistenza ai danni da essi causati."),
                    //Scuola di Ammaliamento
                    Create("Ammaliatore Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Ammaliamento nel proprio libro."),
                    Create("Sguardo Ipnotico",2, "Dal 2° livello, può affascinare una creatura adiacente con uno sguardo. Se fallisce un TS Saggezza, è incapacitata e ferma fino alla fine del turno successivo."),
                    Create("Fascino Istintivo",6, "Dal 6° livello, può usare la reazione per deviare un attacco verso un'altra creatura se l'attaccante fallisce un TS Saggezza. Immuni al fascino ne sono esclusi."),
                    Create("Ammaliamento Condiviso",10, "Dal 10° livello, quando lancia un incantesimo di Ammaliamento su una creatura, può farlo bersagliare anche una seconda."),
                    Create("Alterare Ricordi",14, "Dal 14° livello, può rendere una creatura inconsapevole d'essere stata affascinata."),
                    //Scuola di Divinazione
                    Create("Divinatore Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Divinazione nel proprio libro."),
                    Create("Portento",2, "Dal 2° livello, dopo un riposo lungo, tira 2d20. Può sostituire un tiro visibile con uno dei risultati, una volta per turno."),
                    Create("Divinazione Esperta", 6,"Dal 6° livello, lanciando un incantesimo di divinazione di 2° o più, recupera uno slot speso di livello inferiore (massimo 5°)."),
                    Create("Terzo Occhio",10, "Dal 10° livello, può attivare una vista speciale fino al prossimo riposo: scurovisione, vista eterea, lettura di ogni lingua o vedere invisibilità."),
                    Create("Portento Superiore",14, "Dal 14° livello, tira 3d20 anziché 2 per il privilegio Presagio."),
                    //Scuola di Evocazione
                    Create("Evocatore Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Evocazione nel proprio libro."),
                    Create("Evocazione Minore",2, "Dal 2° livello, può evocare un oggetto non magico visto prima. Dura 1 ora o finché danneggiato."),
                    Create("Trasposizione Benevola",6, "Dal 6° livello, può teletrasportarsi di 9 metri o scambiarsi con una creatura consenziente entro portata. Si ricarica con un riposo lungo o con un incantesimo di Evocazione."),
                    Create("Evocazione Focalizzata",10, "Dal 10° livello, mentre mantiene la concentrazione su incantesimi di Evocazione, non la perde a causa dei danni."),
                    Create("Evocazioni Perduranti", 14,"Dal 14° livello, le creature evocate ottengono 30 punti ferita temporanei."),
                    //Scuola di Illusione
                    Create("Illusionista Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Illusione nel proprio libro."),
                    Create("Illusione Minore Migliorata",2, "Dal 2° livello, apprende Illusione Minore e può creare suono e immagine insieme con un solo lancio."),
                    Create("Illusioni Duttili", 6,"Dal 6° livello, può modificare illusioni della durata di almeno 1 minuto con un'azione, se le può vedere."),
                    Create("Sosia Illusorio", 10,"Dal 10° livello, può usare la reazione per creare un duplicato che fa fallire un attacco. Usabile una volta per riposo breve o lungo."),
                    Create("Realtà Illusoria", 14,"Dal 14° livello, può rendere reale per 1 minuto un oggetto inanimato di un'incantesimo di illusione lanciato. L'oggetto non può infliggere danni o ferire in modo diretto."),
                    //Scuola di Invocazione
                    Create("Invocatore Sapiente", 2,"Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Invocazione nel proprio libro."),
                    Create("Plasmare Incantesimi",2, "Dal 2° livello, può escludere creature visibili dagli effetti dannosi degli incantesimi di Invocazione."),
                    Create("Trucchetto Potente",6, "Dal 6° livello, i trucchetti che infliggono danno fanno metà danno anche se il bersaglio supera il TS."),
                    Create("Invocazione Potente",10, "Dal 10° livello, aggiunge il modificatore di Intelligenza a un tiro di danno degli incantesimi di Invocazione."),
                    Create("Saturazione Magica",14, "Dal 14° livello, può massimizzare il danno di incantesimi di 1°-5° livello. Ripeterlo causa danni necrotici crescenti."),
                    //Scuola di Necromanzia
                    Create("Necromante Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Necromanzia nel proprio libro."),
                    Create("Raccolto Macabro",2, "Dal 2° livello, quando uccide con un incantesimo, recupera PF pari a 2x il livello delliincantesimo (3x se di Necromanzia)."),
                    Create("Servitori Non Morti",6, "Dal 6° livello, con Anima dei Morti evoca 1 non morto extra. I non morti creati ottengono PF bonus e danni aumentati."),
                    Create("Impervio alla Non Morte",10, "Dal 10° livello, ha resistenza ai danni necrotici e il suo massimo di PF non può essere ridotto."),
                    Create("Comandare Non Morti",14, "Dal 14° livello, può soggiogare un non morto entro 18m con TS su Carisma. Se il bersaglio possiede un'Intelligenza pari o superiore a 8, dispone di vantaggio al tiro salvezza."),
                    //Scuola di Trasmutazione
                    Create("Trasmutatore Sapiente",2, "Dal 2° livello, dimezza tempo e costo in oro per trascrivere incantesimi di Trasmutazione nel proprio libro."),
                    Create("Alchimia Minore",2, "Dal 2° livello, può trasformare temporaneamente legno, pietra, ferro, rame o argento in un altro tra questi materiali."),
                    Create("Pietra del Trasmutatore",6, "Dal 6° livello, crea una pietra che conferisce un bonus a scelta (es. resistenza, scurovisione, etc.) finché tenuta."),
                    Create("Mutaforma", 10,"Dal 10° livello, può lanciare Metamorfosi su sé stesso senza slot 1 volta per riposo breve, trasformandosi in una bestia CR 1 o meno."),
                    Create("Maestro Trasmutatore",14, "Dal 14° livello, può consumare la pietra per: trasmutare oggetti, guarire, resuscitare o ringiovanire una creatura."),
                    //Ordine degli Scribi
                    Create("Calamo Magico", 2,"Dal 2° livello, crea una penna magica che scrive senza inchiostro, copia incantesimi in 2 minuti per livello dell'incantesimo e può cancellare qualunque cosa abbia scritto con un'azione bonus."),
                    Create("Libro degli Incantesimi Risvegliato",2, "Dal 2° livello, usa il libro come focus. Può cambiare tipo di danno di un incantesimo, lanciare rituali senza tempo extra (1/g), e trasferirli in un nuovo libro."),
                    Create("Mente Manifestata",6, "Dal 6° livello, evoca una mente spettrale del libro che vede e sente. Quando il mago lancia un incantesimo da mago nel suo turno, pud farlo come se si trovasse nello spazio della mente spettrale, invece che nel proprio, usandone i sensi."),
                    Create("Scrivano Arcano",10, "Dal 10° livello, dopo ogni riposo lungo può creare una pergamena potenziata di un incantesimo di 1° o 2° livello."),
                    Create("Comunione con il Testo",14, "Dal 14° livello, il mago ha vantaggio su Arcano. Può annullare un danno subìto facendo svanire la mente e perdendo incantesimi (3d6 livelli totali)."),
                    //Canto della Lama
                    Create("Arte della Guerra e del Canto", 2,"Dal 2° livello, ottiene competenza in armature leggere, una arma da mischia a una mano a scelta e in Intrattenere."),
                    Create("Melodia della Lama",2, "Dal 2° livello, bonus azione per attivare la Melodia della Lama conferendo dei bonus. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Attacco Extra", 6,"Dal 6° livello, può attaccare due volte con l'azione d'attacco, sostituendo uno degli attacchi con un trucchetto."),
                    Create("Canto di Difesa", 10,"Dal 10° livello, mentre la Melodia della Lama è attivo, può usare una reazione e spendere uno slot per ridurre il danno di 5x il livello dello slot."),
                    Create("Canto di Vittoria",14, "Dal 14° livello, mentre la Melodia della Lama è attivo, aggiunge il modificatore di Intelligenza (min +1) ai danni degli attacchi in mischia."),
                    //Magia della Guerra
                    Create("Deviazione Arcana",2, "Dal 2° livello, il mago può usare la sua reazione per ottenere +2 alla CA contro un attacco o +4 a un tiro salvezza fallito. Dopo l'uso, può lanciare solo trucchetti fino alla fine del turno successivo."),
                    Create("Ingegno Tattico",2, "Dal 2° livello, il mago aggiunge il modificatore di Intelligenza alle prove di iniziativa."),
                    Create("Impulso di Potere",6, "Dal 6° livello, immagazzina cariche magiche (max Int). Ne ottiene 1 al riposo lungo o al successo con Dissolvi Magia/Controincantesimo."),
                    Create("Magia Durevole",10, "Dal 10° livello, mentre mantiene concentrazione su un incantesimo, ottiene +2 alla CA e a tutti i tiri salvezza."),
                    Create("Sudario di Deviazione", 14,"Dal 14° livello, quando usa Deviazione Arcana, infligge danni da forza pari a metà del livello da mago a 3 creature a scelta entro 18m."),
                     
                //LADRO
                    Create("Maestria", 1, "Al 1° livello, raddoppia il bonus di competenza per due abilità, o una abilità e gli strumenti da scasso. Al 6° livello, può scegliere altre due competenze su cui applicare questo beneficio."),
                    Create("Attacco Furtivo", 1, "Al 1° livello, infligge danni extra una volta per turno se ha vantaggio o un alleato è entro 1,5 m dal bersaglio. Il danno extra aumenta con il livello."),
                    Create("Gergo dei Ladri", 1, "Al 1° livello, apprende un linguaggio segreto fatto di slang e simboli usato per comunicare con altri ladri. Serve più tempo per trasmettere i messaggi nascosti."),
                    Create("Azione Scaltra", 2, "Al 2° livello, può usare un'azione bonus ogni turno per Disimpegno, Nascondersi o Scatto, sfruttando la sua agilità per muoversi rapidamente in battaglia."),
                    Create("Archetipo Ladresco", 3, "Al 3° livello, sceglie un archetipo che modella le sue capacità da ladro. Ottiene privilegi aggiuntivi al 3°, 9°, 13° e 17° livello."),
                    Create("Mira Stabile (Opzionale)", 3, "Al 3° livello, può usare un'azione bonus per ottenere vantaggio al prossimo attacco se non si è mosso. Dopo l'uso, la velocità è 0 fino alla fine del turno."),
                    Create("Schivata Prodigiosa", 5, "Al 5° livello, il ladro può usare la reazione per dimezzare i danni di un attacco da una creatura visibile. Richiede che non sia sorpresa o incapacitata."),
                    Create("Evasione", 7, "Al 7° livello, quando il ladro è soggetto a un effetto che gli consente di effettuare un tiro salvezza su Destrezza per dimezzare i danni, non subisce alcun danno se supera il tiro salvezza, e soltanto la metà dei danni se lo fallisce."),
                    Create("Dote Affidabile", 11, "All'11° livello, quando il ladro effettua prove con bonus di competenza, considera i tiri di dado da 1 a 9 come se avesse tirato 10. Garantisce costanza nelle abilità."),
                    Create("Percezione Cieca", 14, "Al 14° livello, se può udire, il ladro percepisce la posizione di creature invisibili o nascoste entro 3 metri."),
                    Create("Mente Sfuggente", 15, "Al 15° livello, questa classe acquisisce competenza nei tiri salvezza su Saggezza, aumentando la sua resistenza mentale contro illusioni e incantesimi mentali."),
                    Create("Inafferrabile", 18, "Al 18° livello, nessun attacco ha vantaggio contro di lui, a meno che non sia incapacitato. Sfugge facilmente anche ai combattenti più abili."),
                    Create("Colpo di Fortuna", 20, "Al 20° livello, una volta per riposo, può trasformare un attacco mancato in un colpo a segno o trattare un fallimento in una prova come un 20 naturale."),
                    //Furfante
                    Create("Mani Veloci", 3, "Al 3° livello, può usare l'azione bonus di Azione Scaltra per effettuare prove di Rapidità di Mano, usare strumenti da scasso o l'azione Usare un Oggetto."),
                    Create("Lavoro al Secondo Piano", 3, "Al 3° livello, salire non costa movimento extra. I salti in corsa coprono più distanza pari al modificatore di Destrezza."),
                    Create("Furtività Suprema", 9, "Al 9° livello, se si muove per metà della sua velocità o meno in un turno, ha vantaggio alle prove di Furtività."),
                    Create("Usare Oggetto Magico", 13, "Al 13° livello, può ignorare requisiti di classe, razza e livello per l'uso degli oggetti magici."),
                    Create("Riflessi da Furfante", 17, "Al 17° livello, agisce due volte nel primo turno di ogni combattimento: una al normale iniziativa, una con iniziativa -10. Non funziona se è sorpreso."),
                    //Assassino
                    Create("Competenze Bonus", 3, "Al 3° livello, ottiene competenza nel kit da travestimento e nel kit da avvelenatore."),
                    Create("Assassinare", 3, "Al 3° livello, ha vantaggio agli attacchi contro creature che non hanno ancora agito. Colpire una creatura sorpresa è un colpo critico automatico."),
                    Create("Maestro Infiltrato", 9, "Al 9° livello, può creare false identità perfette spendendo 7 giorni e 25 mo. Finché non c'è motivo evidente di dubitare, è creduto come autentico."),
                    Create("Impostore", 13, "Al 13° livello, può imitare alla perfezione voce, scrittura e comportamento dopo 3 ore di osservazione. Ha vantaggio su Inganno se qualcuno sospetta."),
                    Create("Colpo Mortale", 17, "Al 17° livello, se colpisce una creatura sorpresa, essa deve fare un tiro salvezza su Costituzione o subisce il doppio del danno dell'attacco."),
                    //Mistificatore Arcano
                    Create("Lancio di Incantesimi", 3, "Al 3° livello, impara a lanciare incantesimi, integrando la magia con l'abilità marziale (per maggiori dettagli consultare il manuale: PHB)."),
                    Create("Gioco di Prestigio della Mano Magica", 3, "Al 3° livello, può rendere invisibile Mano Magica e usarla per manipolare oggetti, usare attrezzi da ladro e agire furtivamente a distanza."),
                    Create("Imboscata Magica", 9, "Al 9° livello, se è nascosto quando lancia un incantesimo, la creatura colpita ha svantaggio ai tiri salvezza contro quell'incantesimo."),
                    Create("Ingannatore Versatile", 13, "Al 13° livello, può usare Mano Magica per distrarre un nemico entro 1,5m. Finché lo fa, ha vantaggio agli attacchi contro di esso fino a fine turno."),
                    Create("Ladro di Incantesimi", 17, "Al 17° livello, se una creatura lo colpisce con un incantesimo, può rubarne la conoscenza. Per 8h può lanciarlo, e il lanciatore originale non può farlo."),
                    //Indagatore
                    Create("Orecchio per l'Inganno", 3, "Al 3° livello, se il ladro fa una prova di Saggezza (Intuizione) per capire se qualcuno mente, tratta i tiri da 1 a 7 come se fossero un 8."),
                    Create("Occhio per i Dettagli", 3, "Al 3° livello, il ladro indagatore può usare un'azione bonus per fare una prova di Saggezza (Percezione) o Intelligenza (Indagare) per scovare indizi o bersagli nascosti."),
                    Create("Combattimento Intuitivo", 3, "Al 3° livello, il ladro può usare un'azione bonus per intuire le tattiche di un nemico. Se ha successo, può applicare Attacco Furtivo anche senza vantaggio per 1 minuto."),
                    Create("Occhio di Lince", 9, "Al 9° livello, se si muove per non più di metà velocità, ha vantaggio alle prove di Percezione e Indagare."),
                    Create("Occhio Infallibile", 13, "Al 13° livello, come azione il ladro può rilevare illusioni, mutaforma e magie ingannevoli entro 9m. Utilizzabile un numero di volte pari al modificatore di Saggezza."),
                    Create("Individuare il Punto Debole", 17, "Al 17° livello, mentre usa Combattimento Intuitivo contro una creatura, i suoi danni da Attacco Furtivo aumentano di 3d6."),
                    //Esploratore
                    Create("Schermagliatore", 3, "Al 3° livello, se un nemico termina il turno entro 1,5 m da lui, può muoversi fino a metà velocità come reazione senza provocare attacchi d'opportunità."),
                    Create("Survivalista", 3, "Al 3° livello, il ladro ottiene competenza in Natura e Sopravvivenza (se non già acquisite) e raddoppia il bonus di competenza su queste prove."),
                    Create("Mobilità Superiore", 9, "Al 9° livello, la velocità di movimento aumenta di 3 metri. L'aumento si applica anche alla velocità di nuoto e scalata, se le possiede."),
                    Create("Maestro di Imboscate", 13, "Al 13° livello, ha vantaggio all'iniziativa. Il primo bersaglio colpito nel primo turno di combattimento subisce attacchi con vantaggio fino al turno successivo."),
                    Create("Colpo Improvviso", 17, "Al 17° livello, se usa l'azione Attacco può fare un attacco extra come azione bonus, che può beneficiare di Attacco Furtivo su un bersaglio diverso."),
                    //Pianificatore
					Create("Maestro degli Intrighi", 3, "Al 3° livello, ottiene competenza con kit di travestimento, kit da falsario e un set da gioco a scelta. Impara due lingue e può imitare accento e parlata di una creatura dopo 1 minuto d'ascolto."),
                    Create("Maestro di Tattiche", 3, "Al 3° livello, può usare l'azione Aiuto come azione bonus. Se aiuta un attacco, il bersaglio deve trovarsi entro 9m da lui, purchè possa vederlo o sentirlo."),
                    Create("Manipolatore Intuitivo", 9, "Al 9° livello, dopo 1 minuto d'osservazione fuori dal combattimento, il ladro può confrontare due caratteristiche (Int, Sag, Car o livelli) con quelle di una creatura."),
                    Create("Fuorviare", 13, "Al 13° livello, se una creatura entro 1,5m gli fornisce copertura, può usare la reazione per far sì che un attacco lo colpisca al posto suo."),
                    Create("Anima Ingannevole", 17, "Al 17° livello, la mente del ladro non può essere letta. Può generare pensieri falsi (Prova di Inganno). Può far apparire ogni bugia come verità e resistere alla magia che obbliga a dire il vero."),
                    //Spadaccino
					Create("Gioco di Gambe", 3, "Dal 3° livello, se colpisce in mischia una creatura durante il turno, essa non può effettuare attacchi di opportunità contro di lui fino alla fine del turno."),
                    Create("Audacia del Fanfarone", 3, "Dal 3° livello, aggiunge il modificatore di Carisma all'iniziativa. Può usare Attacco Furtivo senza vantaggio se è entro 1,5m dal bersaglio, senza altre creature vicine, e non ha svantaggio."),
                    Create("Spavalderia", 9, "Dal 9° livello, può affascinare con Persuasione. Se riesce contro nemici, essi hanno svantaggio verso altri e non fanno attacchi d'opportunità. Se non ostili, vengono ammaliati per 1 minuto."),
                    Create("Manovra Elegante", 13, "Dal 13° livello, può usare un'azione bonus per ottenere vantaggio alla prossima prova di Acrobazia o Atletica fatta nello stesso turno."),
                    Create("Maestro Duellante", 17, "Dal 17° livello, se fallisce un attacco, può ripetere il tiro con vantaggio. Può farlo una volta per riposo breve o lungo."),
                    //Fantasma
                    Create("Sussurri dei Morti", 3, "Dal 3° livello, dopo ogni riposo può ottenere una competenza in un'abilità o attrezzo a scelta, finché non ne sceglie un'altra."),
                    Create("Lamenti dalla Tomba", 3, "Dal 3° livello, dopo un Attacco Furtivo può infliggere danni necrotici (metà dei dadi da Furtivo) a una creatura entro 9m. Il personaggio pud utilizzare questo privilegio un numero di volte pari al valore del suo bonus di competenza e recuperare tutti gli utilizzi spesi quando completa un riposo lungo."),
                    Create("Pegni dei Defunti", 9, "Dal 9° livello, quando una creatura muore entro 9m, può ottenere una Reliquia d'Anima. Può averne fino al proprio bonus di competenza. Le reliquie danno vantaggio ai tiri salvezza su Morte e Costituzione."),
                    Create("Cammino Fantasma", 13, "Dal 13° livello, come azione bonus assume forma spettrale per 10 min: acquisisce una capacità di volo di 3m, attacchi subiti con svantaggio, può passare attraverso creature e oggetti."),
                    Create("Compagno della Morte", 17, "Dal 17° livello, Lamenti dalla Tomba colpisce anche il primo bersaglio. Dopo ogni riposo lungo, se non ha reliquie, ne appare una automaticamente."),
                    //Lama Spirituale
					Create("Potere Psionico", 3, "Dal 3° livello, il ladro possiede un numero di Dadi Energia Psionica pari al doppio del bonus competenza. Può spenderli per attivare poteri speciali. I dadi aumentano a d8 (5°), d10 (11°), d12 (17°)."),
                    Create("Lame Psichiche", 3, "Dal 3° livello, il ladro può evocare una lama mentale come attacco in mischia o a distanza (portata 60m). Danno 1d6 psichico (attacco normale), 1d4 (attacco bonus). Furtivo applicabile."),
                    Create("Lame dell'Anima", 9, "Dal 9° livello, il ladro può usare 1 dado psionico per ritirare un attacco mancato (se colpisce, consuma il dado) o teletrasportarsi lanciando una lama fino a 3m x tiro del dado."),
                    Create("Velo Psichico", 13, "Dal 13° livello, il ladro può diventare invisibile per 1 ora o fino a quando danneggia qualcuno. Utilizzabile una volta per riposo lungo o spendendo 1 dado psionico."),
                    Create("Mente Lacerata", 17, "Dal 17° livello, se infligge Furtivo con Lama Psichica, può forzare un TS su Saggezza. Se fallisce, il bersaglio è stordito per 1 minuto (TS a fine turno per terminare). Una volta per riposo lungo o dopo aver speso 3 dadi."),

                //PALADINO
                    Create("Percezione del Divino", 1, "Dal 1° livello, il paladino può percepire la presenza di celestiali, demoni o non morti entro 18m. Rileva anche oggetti e luoghi consacrati o profanati."),
                    Create("Imposizione delle Mani", 1, "Dal 1° livello, ha una riserva di potere curativo che si ripristina a ogni riposo lungo. Può curare creature fino al massimo della riserva o neutralizzare veleni e malattie."),
                    Create("Stile di Combattimento", 2, "Dal 2° livello, adotta uno stile di combattimento tra le opzioni disponibili per i paladini. Non può scegliere lo stesso stile più di una volta."),
                    Create("Incantesimi Divini", 2, "Dal 2° livello, apprende la magia divina e può lanciare incantesimi come un chierico."),
                    Create("Punizione Divina", 2, "Dal 2° livello, quando colpisce con un'arma in mischia, può spendere uno slot incantesimo per infliggere danni radianti extra (2d8 per uno slot di 1° livello, +1d8 per livello superiore, massimo 5d8)."),
                    Create("Salute Divina", 3, "Dal 3° livello, la magia divina che lo scorre attraverso lo rende immune alle malattie."),
                    Create("Giuramento Sacro", 3, "Dal 3° livello, giura il suo giuramento sacro, ottenendo poteri speciali e incantesimi a livelli 3°, 7°, 15° e 20°. Ottiene anche la capacità Incanalare Divinità."),
                    Create("Imbrigliare il Potere Divino (Opzionale)", 3, "Dal 3° livello, può usare il suo Incanalare Divinità per recuperare uno slot incantesimo, il cui livello è pari a metà del suo bonus competenza (approssimato per eccesso)."),
                    Create("Versatilità Marziale (Opzionale)", 4, "Ogni volta che ottiene un miglioramento delle abilità, può sostituire uno stile di combattimento con un altro disponibile per il paladino."),
                    Create("Aura di Protezione", 6, "Dal 6° livello, conferisce un bonus ai TS di un alleato entro 3m pari al suo modificatore di Carisma. La portata aumenta a 9m al 18° livello."),
                    Create("Aura di Coraggio", 10, "Dal 10° livello, protegge se stesso e gli alleati entro 3m da effetti che li fanno spaventare. La portata aumenta a 9m al 18° livello."),
                    Create("Punizione Divina Migliorata", 11, "Dal 11° livello, tutti gli attacchi con un'arma in mischia infliggono 1d8 danni radianti extra."),
                    Create("Tocco Purificatore", 14, "Dal 14° livello, può usare un'azione per rimuovere un incantesimo su di sé o su una creatura volontaria che tocca. Può usare questa capacità pari al suo modificatore di Carisma (minimo 1 volta)."),
                    //Giuramento degli Antichi
					Create("Dettami degli Antichi", 3, "Dal 3° livello, attraverso atti di misericordia, gentilezza e perdono, accende la luce della speranza nel mondo, respingendo la disperazione."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi specifici per il Giuramento degli Antichi, che enfatizzano la luce e la protezione."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Aura di Protezione e Sentinella Immortale."),
                    Create("Aura di Interdizione", 7, "Dal 7° livello, la magia antica crea un'aura che conferisce resistenza ai danni da incantesimi a lui e agli alleati entro 3m. La portata aumenta a 9m al 18° livello."),
                    Create("Sentinella Imperitura", 15, "Dal 15° livello, quando ridotto a 0 punti ferita, può scegliere di rimanere a 1 punto ferita. Non subisce gli effetti negativi dell'invecchiamento e non può essere invecchiato magicamente."),
                    Create("Campione degli Antichi", 20, "Dal 20° livello, può trasformarsi in una forma antica della natura per 1 minuto, ottenendo: rigenerazione di 10 punti ferita, possibilità di lanciare incantesimi come azione bonus e svantaggio sui TS nemici contro i suoi incantesimi e Incanalare Divinità."),
                    //Giuramento di Devozione
                    Create("Dettami di Devozione", 3, "Dal 3° livello, attraverso atti di onestà, coraggio e onore, agisce in nome della giustizia e del bene superiore."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi specifici per il Giuramento della Devozione, focalizzati su protezione e giustizia."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Arma Consacrata e Scacciare i Sacrileghi."),
                    Create("Aura di Devozione", 7, "Dal 7° livello, il paladino e i suoi alleati entro 3m sono immuni agli incantesimi di charme. La portata aumenta a 9m al 18° livello."),
                    Create("Purezza di Spirito", 15, "Dal 15° livello, il paladino è sempre sotto l'effetto dell'incantesimo Protezione dal Bene e dal Male."),
                    Create("Nimbo Sacro", 20, "Dal 20° livello, può emanare luce solare in un raggio di 9m, infliggendo 10 danni radianti ai nemici che iniziano il loro turno nella luce. Inoltre, ha vantaggio contro gli incantesimi di immondi e non-morti."),
                    //Giuramento della Vendetta
					Create("Dettami di Vendetta", 3, "Dal 3° livello, il paladino cerca la vendetta per le ingiustizie, perseguendo i colpevoli e punendo i malvagi."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi per infliggere danni e inseguire i malvagi."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Il Giuramento di Inimicizia e Abiurare Nemico."),
                    Create("Giuramento della Vendetta", 3, "Dal 3° livello, scegli un nemico giurato e infliggi danni maggiori quando ti concentri su di lui."),
                    Create("Vendicatore Implacabile", 7, "Dal 7° livello, il paladino può usare un'azione per ottenere vantaggio su attacchi contro il suo nemico giurato."),
                    Create("Anima di Vendetta", 15, "Dal 15° livello, il paladino infligge danni radianti ogni volta che il nemico giurato lo colpisce."),
                    Create("Angelo Vendicatore", 20, "Dal 20° livello, quando il paladino infligge danni al nemico giurato, può infliggere danni extra in base al danno subito."),
                    //Giuramento della Conquista
					Create("Dettami di Conquista", 3, "Dal 3° livello, il paladino mira a imporre la sua volontà sul mondo, cercando di domare gli altri attraverso il terrore."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi che permettono di esercitare il dominio e la paura sugli altri."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Governare con Pugno di Ferro e La Forza Prima di Tutto."),
                    Create("Aura di Conquista", 7, "Dal 7° livello, il paladino e i suoi alleati entro 3m hanno vantaggio sui tiri per colpire contro nemici intimiditi. La portata aumenta a 9m al 18° livello."),
                    Create("Intimorire Sprezzante", 15, "Dal 15° livello, il paladino può imporsi su creature che hanno subito danni, forzandole a obbedire."),
                    Create("Conquistatore Invincibile", 20, "Dal 20° livello, il paladino può emanare paura a tutti i nemici entro 9m, costringendoli a fuggire per 1 minuto."),
                    //Giuramento della Gloria
					Create("Dettami di Gloria", 3, "Dal 3° livello, il paladino incarna la gloria della lotta per la giustizia, ispirando i suoi compagni a combattere con coraggio."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi che potenziano il morale e la forza dei compagni."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Atleta Impareggiabile e Punizione Ispiratrice."),
                    Create("Aura di Alacrità", 7, "Dal 7° livello, il paladino e gli alleati entro 3m ottengono vantaggio nei tiri salvezza contro il terrore. La portata aumenta a 9m al 18° livello."),
                    Create("Difesa Gloriosa", 15, "Dal 15° livello, Il paladino può trasformare una mossa difensiva in un colpo repentino. Quando il paladino o una creatura che riesce a vedere entro 3 metri da lui subiscono un tiro per colpire, pud usare la sua reazione per conferire un bonus alla CA del bersaglio contro quell'attacco, in modo da aumentare le possibilita che il colpo non vada a segno."),
                    Create("Leggenda Vivente", 20, "Dal 20° livello, il paladino può ispirare tutti i suoi alleati con un'azione, aumentando i loro danni e resistenza."),
                    //Giuramento della Redenzione
					Create("Dettami di Redenzione", 3, "Dal 3° livello, il paladino cerca di redimere i malvagi, piuttosto che punirli, perseguendo la pace."),
                    Create("Incantesimi del Giuramento", 3, "Dal 3° livello, ottiene incantesimi che favoriscono la guarigione e la pacificazione."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Emissario di Pace e Intimorire i Violenti"),
                    Create("Aura del Guardiano", 7, "Dal 7° livello, il paladino può emettere un'aura di pace che riduce il danno inflitto da attacchi."),
                    Create("Sprito Protettivo", 15, "Dal 15° livello, il paladino può cancellare la malvagità da un nemico, riducendo il danno pari a 1d6 + la metà del livello da paladino favorendo la redenzione."),
                    Create("Emissario di Redenzione", 20, "Dal 20° livello, il paladino può usare una preghiera per purificare un nemico, rimuovendo ogni malvagità e danno."),
                    //Giuramento dell'Apostata
                    Create("Fede Negata", 3, "Dal 3° livello, l'apostata rinnega il suo giuramento e il suo dio, scegliendo il potere personale."),
                    Create("Incantesimi dell'Apostata", 3, "Dal 3° livello, ottiene incantesimi che permettono di combattere utilizzando poteri oscuri."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Controllare non-morti e Aspetto Terrificante."),
                    Create("Aura di Odio", 7, "Dal 7° livello, qualsiasi demone e non morto a meno di 3m da lui, guadagna un bonus per i danni da danno dell'arma in mischia pari al tuo modificatore di carisma (minimo di +1). Una creatura può beneficiare di questa funzione di un solo paladino alla volta."),
                    Create("Resistenza Soprannaturale", 15, "Dal 15° livello, l'apostata guadagna resistenza ai danni contundenti, perforanti e taglienti da armi non magiche."),
                    Create("Abisso Incontenibile", 20, "Dal 20° livello, l'apostata può usare una volta il potere oscuro per infliggere danni devastanti a tutti i nemici vicini."),
                    Create("Signore del Terrore", 20, "Al 20° livello, crea un'aura di oscurità (9m) per 1 minuto. Nemici spaventati subiscono 4d10 danni psichici all'inizio del turno. Alleati nell'aura sono oscurati. Azione bonus: attacco ombra (3d10+Carisma danni necrotici). Utilizzabile 1/riposo lungo."),
                    //Giuramento delle Sentinelle
                    Create("Dettami delle Sentinelle", 3, "Giuramento volto a proteggere il mondo dalle minacce extraplanari."),
                    Create("Incantesimi del Giuramento", 3, "Lista di incantesimi specifici ottenuti ai livelli 3°, 5°, 9°, 13° e 17° (es. Rilevare magia, Protezione dal male, Scudo di fede)."),
                    Create("Incanalare Divinità", 3, "Dal 3° livello, ottiene due opzioni di Incanalare Divinità: Volontà della Sentinella e Abiurare Creature Extraplanari"),
                    Create("Aura della Sentinella", 7, "A partire dal 7° livello, il paladino emana un'aura di 3 metri (9 metri al livello 18) che garantisce a lui e ai suoi alleati un bonus all'iniziativa pari al suo bonus di competenza."),
                    Create("Punizione Solerte", 15, "Dal 15° livello, quando il paladino o un alleato entro 9 metri supera un tiro salvezza mentale, può reagire infliggendo 2d8 + modificatore di Carisma danni da forza all'aggressore."),
                    Create("Baluardo dei Mortali", 20, "Al 20° livello, il paladino può attivare per 1 minuto la vista pura (36m), ottiene vantaggio contro extraplanari e la capacità di bandirli colpendoli. Utilizzabile una volta per riposo lungo."),
                
                //RANGER
                    Create("Nemico Prescelto", 1, "Al 1° livello, il ranger sceglie un tipo di creatura come nemico preferito. Ottiene vantaggio sui tiri di Saggezza (Sopravvivenza) per seguirli e su Intelligenza per ricordare informazioni. Impara inoltre una lingua parlata da tali creature."),
                    Create("Avversario Prescelto (Opzionale)", 1, "(Privilegio che sostituisce Nemico Prescelto) Dal 1° livello, il ranger può segnare una creatura colpita come nemico per 1 minuto. Una volta per turno, infligge 1d4 danni extra. Utilizzabile un numero di volte pari al bonus competenza."),
                    Create("Esploratore Nato", 1, "Al 1° livello, il ranger sceglie un terreno preferito. In tale ambiente ottiene vantaggi esplorativi: viaggia più velocemente, non si perde, trova più cibo e individua tracce con precisione."),
                    Create("Esploratore Esperto (Opzionale)", 1, "Privilegio che sostituisce Esploratore Nato) Dal 1° livello, il ranger raddoppia il bonus competenza in un'abilità scelta e impara 2 lingue. A livelli superiori ottiene velocità di arrampicata/nuoto e punti ferita temporanei."),
                    Create("Stile di Combattimento", 2, "Al 2° livello, il ranger sceglie uno stile di combattimento specializzato tra quelli disponibili."),
                    Create("Incantesimi", 2, "Dal 2° livello, il ranger impara a lanciare incantesimi della natura."),
                    Create("Consapevolezza Primordiale", 3, "Al 3° livello, spendendo uno slot incantesimo, il ranger può percepire la presenza di creature speciali (es: draghi, non-morti) entro 1,5 km (9 km nel terreno prescelto) per 1 minuto per livello dello slot."),
                    //----> Attacco Extra
                    Create("Andatura sul Territorio", 8, "All'8° livello, il ranger ignora il terreno difficile non magico e le piante naturali. Ottiene vantaggio contro effetti magici che ostacolano il movimento."),
                    Create("Nascondersi in piena vista", 10, "Al 10° livello, il ranger può mimetizzarsi per un 1 minuto al fine di ottenere +10 a Furtività se rimane immobile. La versione opzionale concede invece invisibilità come azione bonus."),
                    Create("Svanire", 14, "Dal 14° livello, il ranger può nascondersi come azione bonus e non può essere seguito con mezzi non magici."),
                    Create("Sensi Ferini", 18, "Al 18° livello, il ranger attacca senza svantaggio le creature invisibili e ne percepisce la posizione entro 9m."),
                    Create("Sterminatore di Nemici", 20, "Al 20° livello, il ranger aggiunge il suo modificatore di Saggezza a un tiro di attacco o danno per turno contro i nemici preferiti."),
                    //Cacciatore
                    Create("Preda del Cacciatore", 3, "Al 3° livello, il ranger sceglie una tecnica di caccia: Abbattitore di Colossi (+1d8 danni a creature ferite), Uccisore di Giganti (reazione per contrattaccare creature grandi) o Spezzatore di Orde (attacco aggiuntivo contro bersagli vicini)."),
                    Create("Tattiche Difensive", 7, "Al 7° livello, il ranger sceglie una difesa speciale: Difesa dal Multiattacco, Sfuggire all'Orda o Volontà d'Acciaio.(per maggiori dettagli consultare il manuale: PHB)"),
                    Create("Multiattacco", 11, "All'11° livello, il ranger sceglie una tecnica: Raffica o Attacco Turbinante."),
                    Create("Difesa del Cacciatore Superiore", 15, "Al 15° livello, il ranger sceglie una difesa finale: Elusione, Opporsi alla Marea o Schivata Prodigiosa."),
                    //Signore delle Bestie
                    Create("Compagno del Ranger", 3, "Al 3° livello, il ranger ottiene un compagno animale (GF 1/4 o inferiore). L'animale combatte al suo fianco, condivide l'iniziativa e può essere comandato in battaglia. Ottiene bonus di competenza e PF aumentati. Può essere sostituito con 8 ore di legame magico se muore."),
                    Create("Compagno Primordiale (Opzionale)", 3, "(Privilegio che sostituisce Compagnoico del Ranger) Dal 3° livello, il ranger evoca un compagno magico (Terra, Mare o Cielo). Può essere comandato con un'azione bonus e rianimato spendendo slot incantesimo. Scompare dopo un riposo lungo o alla morte del ranger."),
                    Create("Addestramento Straordinario", 7, "Al 7° livello, il ranger può usare un'azione bonus per far compiere azioni non offensive al compagno. Gli attacchi dell'animale superano le resistenze al danno non magico."),
                    Create("Furia Bestiale", 11, "All'11° livello, quando comandato ad attaccare, il compagno animale effettua due attacchi o usa Multiattacco se disponibile."),
                    Create("Condividere Incantesimi", 15, "Al 15° livello, gli incantesimi lanciati sul ranger possono essere estesi al compagno animale se entro 9 metri."),
                    //Viandante Fatato
                    Create("Colpi Terribili", 3, "Al 3° livello, il ranger può infliggere 1d4 danni psichici aggiuntivi una volta per turno quando colpisce con un'arma. Il danno aumenta a 1d6 all'11° livello."),
                    Create("Magia del Viandante Fatato", 3, "Dal 3° livello, il ranger impara incantesimi aggiuntivi a livelli specifici (nella tabella degli Incantesimi del Viandante Fatato). Questi non contano nel numero di incantesimi conosciuti."),
                    Create("Fascino Ultraterreno", 3, "Al 3° livello, il ranger aggiunge il suo modificatore di Saggezza ai tiri di Carisma (minimo +1). Ottiene inoltre competenza in una abilità a scelta tra Inganno, Intrattenere o Persuasione."),
                    Create("Scambio Seducente", 7, "Dal 7° livello, il ranger ha vantaggio contro effetti che causano Ammaliamento o Paura. (per maggiori dettagli consultare il manuale: TCOE)"),
                    Create("Rinforzi Fatati", 11, "All'11° livello, il ranger ottiene l'incantesimo Evoca Folletto. Può lanciarlo 1/riposo lungo senza slot e senza concentrazione (durata 1 minuto)."),
                    Create("Viandante Velato", 15, "Al 15° livello, il ranger può lanciare Passo Velato senza slot un numero di volte pari al modificatore di Saggezza. Può portare con sé un alleato quando si teletrasporta."),
                    //Custode degli Sciami
                    Create("Sciame Riunito", 3, "Al 3° livello, uno sciame di spiriti naturali ti assiste in combattimento. Una volta per turno, dopo aver colpito un bersaglio, il ranger può: infliggere 1d6 danni perforanti, spostare il bersaglio di 4.5m (tiro salvezza Forza fallito) o spostarti di 1.5m."),
                    Create("Magia del Custode degli Sciami", 3, "Dal 3° livello, il ranger apprende il trucchetto Mano Magica (assume la forma del tuo sciame) e incantesimi aggiuntivi dalla tabella degli Incantesimi del Custode degli Sciami a livelli specifici."),
                    Create("Marea Avvolgente", 7, "Dal 7° livello, può usare un'azione bonus per ottenere volo 3m e capacità di librarsi per 1 minuto. Utilizzabile un numero di volte pari al bonus competenza."),
                    Create("Sciame Possente", 11, "All'11° livello, lo Sciame Riunito diventa più potente: danni aumentati a 1d8, può far cadere a terra i bersagli spostati e ottieni mezza copertura quando lo sciame ti sposta."),
                    Create("Dispersione Brulicante", 15, "Al 15° livello, il ranger può reagire al danno teletrasportandosi fino a 9m e ottenendo resistenza a quel danno. Utilizzabile un numero di volte pari al bonus competenza."),
                    //Cacciatore delle Tenebre
                    Create("Magia del Cacciatore delle Tenebre", 3, "Dal 3° livello, il ranger impara incantesimi aggiuntivi dalla tabella specifica. Questi incantesimi non contano nel numero massimo di incantesimi conosciuti."),
                    Create("Imboscata Terrificante", 3, "Al 3° livello, il ranger aggiunge il modificatore di Saggezza all'iniziativa. Nel primo turno di combattimento, guadagna 3m di movimento e un attacco aggiuntivo che infligge 1d8 danni extra se colpisce."),
                    Create("Vista dell'Ombra", 3, "Al 3° livello, il ranger ottiene scurovisione 18m (o +9m se già posseduta). Nell'oscurità, risulta invisibile alle creature che dipendono dalla scurovisione."),
                    Create("Mente di Ferro", 7, "Dal 7° livello, il ranger ottiene competenza nei tiri salvezza su Saggezza (o a scelta su Intelligenza/Carisma se già competente)."),
                    Create("Raffica del Cacciatore", 11, "All'11° livello, il ranger può ripetere un attacco fallito una volta per turno, trasformando un errore in un'opportunità."),
                    Create("Schivata dell'Ombra", 15, "Al 15° livello, il ranger può usare una reazione per imporre svantaggio a un attacco contro di lui, purché l'attaccante non abbia già vantaggio."),
                    //Viandante dell'Orizzonte
                    Create("Magia del Viandante degll'Orizzonte", 3, "Dal 3° livello, il ranger impara incantesimi aggiuntivi dalla tabella specifica. Questi incantesimi non contano nel numero massimo di incantesimi conosciuti."),
                    Create("Individuazione dei Portali", 3, "Al 3° livello, il ranger può individuare il portale planare più vicino entro 1,5 km come azione. Utilizzabile 1 volta per riposo breve o lungo."),
                    Create("Combattente Planare", 3, "Al 3° livello, il ranger può convertire i danni di un attacco in danno da forza, aggiungendo 1d8 danni extra (2d8 all'11° livello)."),
                    Create("Passo Etereo", 7, "Dal 7° livello, il ranger può lanciare Forma Eterea come azione bonus senza slot, limitato al turno corrente. Utilizzabile 1 volta per riposo."),
                    Create("Colpo Distante", 11, "All'11° livello, il ranger può teletrasportarsi 3m prima di ogni attacco. Se colpisce due bersagli diversi, ottiene un attacco aggiuntivo contro un terzo."),
                    Create("Difesa Spettrale", 15, "Al 15° livello, il ranger può usare una reazione per ottenere resistenza a tutti i danni di un attacco subito in quel turno."),
                    //Uccisore di Mostri
                    Create("Magia dell'Uccisore di Mostri", 3, "Dal 3° livello, il ranger impara incantesimi aggiuntivi dalla tabella specifica. Questi incantesimi non contano nel numero massimo di incantesimi conosciuti."),
                    Create("Percezione del Cacciatore", 3, "Al 3° livello, il ranger può analizzare una creatura entro 18m come azione, scoprendone immunità, resistenze e vulnerabilità. Utilizzabile un numero di volte pari al modificatore di Saggezza."),
                    Create("Preda dell'Uccisore", 3, "Al 3° livello, il ranger può designare una creatura entro 18m come azione bonus. La prima volta che la colpisce ogni turno, infligge 1d6 danni aggiuntivi. Durata: fino al riposo."),
                    Create("Difesa Soprannaturale", 7, "Dal 7° livello, il ranger aggiunge 1d6 ai tiri salvezza contro la sua Preda e alle prove per sfuggire alla sua presa."),
                    Create("Nemesi degli Incantatori", 11, "All'11° livello, il ranger può usare una reazione per interrompere un incantesimo o teletrasporto entro 18m (tiro salvezza Saggezza fallito). Utilizzabile 1/riposo."),
                    Create("Contrattacco dell'Uccisore", 15, "Al 15° livello, il ranger può reagire a un tiro salvezza contro la sua Preda con un attacco: se colpisce, il tiro salvezza automaticamente riesce."),

                //STREGONE
                    Create("Incantesimi", 1, "Al 1° livello, lo Stregone sviluppa abilità magiche innate, ereditate da un evento arcano del suo passato o della sua stirpe. Questa fonte interiore di magia alimenta tutti i suoi incantesimi."),
                    Create("Origine Stregonesca", 1, "Al 1° livello, lo Stregone sceglie l'origine del suo potere magico innato. La scelta garantisce abilità speciali ai livelli 1, 6, 14 e 18, riflettendo la natura della sua magia."),
                    Create("Fonte di Magia", 2, "Al 2° livello, il Stregone impara a manipolare i Punti di Stregoneria (PS). Può convertirli in slot incantesimo o viceversa come azione bonus. Ottiene 2 PS iniziali, che aumentano con i livelli."),
                    Create("Metamagia", 3, "Dal 3° livello, il Stregone sceglie 2 opzioni di Metamagia per modificare i suoi incantesimi. Ottiene un'opzione aggiuntiva ai livelli 10 e 17."),
                    Create("Stregoneria Versatile (Opzionale)", 4, "Quando ottiene un incremento di caratteristica, il Stregone può sostituire un'opzione di Metamagia o un trucchetto con un altro dalla lista degli stregoni."),
                    Create("Guida Magica (Opzionale)", 5, "Dal 5° livello, il Stregone può spendere 1 PS per ritirare un tiro di abilità fallito, usando il nuovo risultato."),
                    Create("Ripristino Stregonesco", 20, "Al 20° livello, il Stregone recupera 4 PS ogni volta che completa un riposo breve."),
                    //Discendenza Draconica
                    Create("Antenato Draconico", 1, "Al 1° livello, lo Stregone sceglie un tipo di drago come antenato, acquisendo la lingua draconica e vantaggio nei tiri di Carisma per interagire con i draghi (il bonus competenza raddoppia)."),
                    Create("Resilienza Draconica", 1, "Al 1° livello, lo Stregone ottiene +1 PF per livello e una pelle squamosa che garantisce CA 13+Destrezza senza armatura. Il massimo di PF aumenta di 1 ad ogni livello."),
                    Create("Affinità Elementale", 6, "Dal 6° livello, lo Stregone aggiunge il modificatore di Carisma ai danni degli incantesimi del tipo del drago scelto. Può spendere 1 PS per ottenere resistenza a quel danno per 1 ora."),
                    Create("Ali di Drago", 14, "Al 14° livello, lo Stregone può evocare ali da drago come azione bonus, ottenendo volo con velocità pari al movimento. Le ali persistono finché non vengono ritirate."),
                    Create("Presenza Draconica", 18, "Al 18° livello, lo Stregone può spendere 5 PS per emanare un'aura di timore/fascino (18m). I nemici devono superare un tiro salvezza su Saggezza o subiscono l'effetto per 1 minuto."),
                    //Magia Selvaggia
                    Create("Impulso di Magia Selvaggia", 1, "Dal 1° livello, dopo aver lanciato un incantesimo da stregone di 1° livello o superiore, il DM può farti tirare un d20. Con un 1, scatena un effetto casuale dalla tabella degli effetti di magia selvaggia."),
                    Create("Onde di Caos", 1, "Al 1° livello, lo stregone può ottenere vantaggio su un tiro di attacco, abilità o tiro salvezza. Dopo l'uso, il DM può innescare un Impulso quando lanci incantesimi per ripristinare questa abilità."),
                    Create("Piegare la Fortuna", 6, "Dal 6° livello, lo stregone può spendere 2 PS come reazione per aggiungere o sottrarre 1d4 al tiro di un'altra creatura, dopo aver visto il risultato ma prima degli effetti."),
                    Create("Caos Controllato", 14, "Al 14° livello, quando tiri sulla tabella degli effetti di magia selvaggia, puoi tirare due volte e scegliere il risultato preferito."),
                    Create("Bombardamento Magico", 18, "Al 18° livello, quando ottieni il risultato massimo su un dado di danno, puoi rilanciarlo e aggiungere il nuovo risultato. Utilizzabile una volta per turno."),
                    //Magia delle Ombre
                    Create("Peculiarità dello Stregone delle Ombre", 1, "Lo stregone può manifestare particolarità fisiche o comportamentali, determinate dalla tabella delle Stranezze dell'Ombra."),
                    Create("Occhi dell'Oscuro", 1, "Al 1° livello, lo stregone ottiene visione oscura 36m. Dal 3° livello, può lanciare Oscurità spendendo 2 PS o slot, vedendo attraverso di essa se usati PS."),
                    Create("Forza della Tomba", 1, "Dal 1° livello, quando ridotto a 0 PF, lo Stregone può tirare un tiro salvezza su Carisma (CD 5 + danno subito) per stabilizzarsi a 1 PF. Non funziona con danni radianti o colpi critici."),
                    Create("Segugio dei Presagi Nefasti", 6, "Dal 6° livello, lo stregone può spendere 3 PS come azione bonus per evocare un Segugiodei presagi nefasti (statistiche da lupo selvaggio modificato) che dà svantaggio ai tiri salvezza del bersaglio."),
                    Create("Camminare nelle Ombre", 14, "Al 14° livello, lo stregone può teletrasportarsi 36m tra aree di luce fioca o oscurità come azione bonus."),
                    Create("Forma d'Ombra", 18, "Al 18° livello, lo stregone può spendere 6 PS per assumere una forma d'ombra (resistenza a tutti i danni tranne forza/radianti) per 1 minuto, attraversando oggetti e creature."),
                    //Anima Divina
                    Create("Magia Divina", 1, "Al 1° livello, lo Stregone Divino può scegliere incantesimi dalla lista del chierico. Deve inoltre selezionare un'affinità divina (bene, male, legge, caos o neutralità) che determina un incantesimo bonus."),
                    Create("Prescelto dagli Dei", 1, "Dal 1° livello, lo Stregone Divino può aggiungere 2d4 a un tiro di tiro salvezza fallito o a un attacco mancato. Utilizzabile 1 volta per riposo breve o lungo."),
                    Create("Guarigione Potenziata", 6, "Dal 6° livello, lo Stregone Divino può spendere 1 PS per far ritirare i dadi di guarigione a sé o un alleato entro 1.5m. Utilizzabile 1 volta per turno."),
                    Create("Ali Ultraterrene", 14, "Al 14° livello, lo Stregone Divino può evocare ali spettrali (azione bonus) che concedono volo 9m. L'aspetto delle ali dipende dall'affinità divina scelta."),
                    Create("Recupero Ultraterreno", 18, "Al 18° livello, lo Stregone Divino può rigenerare metà dei PF massimi come azione bonus quando sotto il 50% dei PF. Utilizzabile 1 volta per riposo lungo."),
                    //Stregoneria della Tempesta
                    Create("Portavoce del Vento", 1, "Al 1° livello, lo Stregone della Tempesta conosce il Primordiale e può comunicare con chi parla i suoi dialetti. La sua magia è intrisa di energia elementale dell'aria."),
                    Create("Magia Tempestosa", 1, "Dal 1° livello, lo Stregone della Tempesta può volare 3m come azione bonus prima/dopo aver lanciato un incantesimo di 1° livello o superiore, senza provocare attacchi di opportunità."),
                    Create("Cuore della Tempesta", 6, "Dal 6° livello, lo Stregone della Tempesta ottiene resistenza a fulmine e tuono. Quando lancia incantesimi di questi danni, infligge danni aggiuntivi pari a metà del suo livello a creature entro 3m."),
                    Create("Guida della Tempesta", 6, "Dal 6° livello, lo Stregone della Tempesta può controllare localmente pioggia (in un'area di 6m) e vento (in un'area di 30m), modificandone la direzione ma non la velocità."),
                    Create("Furia della Tempesta", 14, "Al 14° livello, lo Stregone della Tempesta può reagire a un attacco in mischia infliggendo danni da fulmine pari al suo livello e spingendo il nemico fino a 6m con un tiro salvezza su Forza fallito."),
                    Create("Anima del Vento", 18, "Al 18° livello, lo Stregone della Tempesta diventa immune a fulmine e tuono, ottiene volo 18m e può concedere volo 9m a 3 + mod. Carisma alleati per 1 ora, una volta per riposo."),
                    //Mente Aberrante
                    Create("Incantesimi Psionici", 1, "Dal 1° livello, lo stregone impara incantesimi aggiuntivi dalla tabella specifica. Può sostituirli quando aumenta di livello, scegliendo tra divinazione e incantamento dalle liste di stregone, warlock o mago."),
                    Create("Conversazione Telepatica", 1, "Al 1° livello, lo stregone può stabilire un collegamento mentale con una creatura entro 9m. La comunicazione dura minuti pari al livello e si estende per miglia pari al modificatore di Carisma."),
                    Create("Stregoneria Psionica", 6, "Dal 6° livello, lo stregone può lanciare incantesimi psionici spendendo PS invece di slot, ignorando componenti verbali, somatici e materiali (se non consumati)."),
                    Create("Difese Psichiche", 6, "Dal 6° livello, lo stregone ottiene resistenza ai danni psichici e vantaggio contro effetti che causano Ammaliamento o Paura."),
                    Create("Rivelazione della Carne", 14, "Al 14° livello, lo stregone può spendere PS per trasformarsi: vedere l'invisibile (18m), volare, nuotare/respirare sott'acqua, o attraversare spazi stretti. Durata: 10 minuti."),
                    Create("Implosione Distorcente", 18, "Al 18° livello, lo stregone può teletrasportarsi 36m, creando un vortice che infligge 3d10 danni da forza e attira i nemici entro 9m. Utilizzabile 1/riposo lungo o spendendo 5 PS."),
                    //Anima Meccanica
                    Create("Magia Meccanica", 1, "Al 1° livello, lo stregone impara incantesimi aggiuntivi dalla tabella specifica. Può sostituirli con altri di abiurazione o trasmutazione dalle liste di stregone, warlock o mago."),
                    Create("Ripristino dell'Equilibrio", 1, "Dal 1° livello, lo stregone può usare una reazione per annullare vantaggio/svantaggio su un tiro di una creatura entro 18m. Utilizzabile un numero di volte pari al bonus competenza."),
                    Create("Bastione della Legge", 6, "Dal 6° livello, lo stregone può spendere 1-5 PS per creare uno scudo protettivo (d8 per PS spesi) su sé stesso o un alleato entro 9m, riducendo i danni subiti."),
                    Create("Trance dell'Ordine", 14, "Al 14° livello, lo stregone può entrare in uno stato di perfetto ordine (bonus azione): per 1 minuto, i tiri contro di lui non hanno vantaggio e i suoi tiri sotto 10 diventano 10."),
                    Create("Cavalleria Meccanica", 18, "Al 18° livello, lo stregone evoca spiriti dell'ordine in un cubo di 9m: rigenera 100 PF totali, ripara oggetti e termina effetti magici di 6° livello o inferiore. Utilizzabile 1/riposo lungo o spendendo 7 PS."),
                
                //WARLOCK
                    Create("Patrono Ultraterreno", 1, "Al 1° livello, il warlock stringe un patto con un'entità extraplanare che gli conferisce abilità speciali ai livelli 1, 6, 10 e 14. La scelta del patrono influenza le capacità ottenute."),
                    Create("Magia del Patto", 1, "Dal 1° livello, il warlock lancia incantesimi usando Carisma come caratteristica. Rigenera gli slot incantesimo dopo un riposo breve o lungo. Conosce 2 trucchetti iniziali e 2 incantesimi di 1° livello."),
                    Create("Suppliche Occulte", 2, "Dal 2° livello, il warlock impara 2 invocazioni che conferiscono abilità magiche permanenti. Può sostituirle quando aumenta di livello. (per maggiori dettagli consultare i manuali: PHB, TCOE)"),
                    Create("Dono del Patto", 3, "Al 3° livello, il warlock riceve un dono dal patrono: Patto della Lama (crea un'arma magica), Patto della Catena (evoca un famiglio speciale), Patto del Tomo (grimorio con 3 trucchetti) o Patto del Talismano (amuleto che aiuta nei tiri abilità)."),
                    Create("Versatilità Occulta (Opzionale)", 4, "Quando ottiene un aumento di caratteristica, il warlock può sostituire un trucchetto, cambiare Dono del Patto o modificare un Arcanum Mistico (se di livello 12+)."),
                    Create("Arcanum Mistico", 11, "Dall'11° livello, il warlock impara un incantesimo di 6° livello che può lanciare 1/riposo lungo senza slot. A livelli superiori impara incantesimi di 7°, 8° e 9° livello."),
                    Create("Maestro dell'Occulto", 20, "Al 20° livello, il warlock può ripristinare tutti gli slot incantesimo dopo 1 minuto di preghiera al patrono. Utilizzabile 1/riposo lungo."),
                    //Patto del signore fatato
                    Create("Presenza Fatata", 1, "Al 1° livello, il warlock può influenzare le creature in un cubo di 3m: con un'azione, obbliga i bersagli a un salvataggio su Saggezza per resistere a Charme o Paura fino al suo prossimo turno. Utilizzabile 1/riposo."),
                    Create("Fuga Velata", 6, "Dal 6° livello, il warlock può reagire ai danni teletrasportandosi 18m e diventando invisibile fino al turno successivo o all'uso di attacchi/incantesimi. Utilizzabile 1/riposo."),
                    Create("Difese Seducenti", 10, "Al 10° livello, il warlock è immune a Charme e può ribaltare l'effetto sul provocatore con una reazione (salvataggio su Saggezza fallito: Charme per 1 minuto)."),
                    Create("Delirio Oscuro", 14, "Al 14° livello, il warlock può intrappolare una creatura entro 18m in un'illusione (salvataggio su Saggezza fallito: Charme o Paura per 1 minuto o fino a perdita di concentrazione). Utilizzabile 1/riposo."),
                    //Patto del celestiale
                    Create("Trucchetti Bonus", 1, "Al 1° livello, il warlock  impara i trucchetti Luce e Fiamma Sacra senza che contino tra quelli conosciuti. Questi sono considerati trucchetti da warlock."),
                    Create("Luce Guaritrice", 1, "Dal 1° livello, il warlock  può usare un'azione bonus per guarire una creatura entro 18m, spendendo d6 da un pool (1 + livello warlock). Rigenera tutti i d6 dopo un riposo lungo."),
                    Create("Anima Radiosa", 6, "Dal 6° livello, il warlock  ottiene resistenza ai danni radianti e aggiunge il modificatore di Carisma ai danni radianti o da fuoco degli incantesimi."),
                    Create("Resistenza Celestiale", 10, "Al 10° livello, il warlock  ottiene PF temporari dopo ogni riposo (livello + Carisma) e può concederne la metà a fino a 5 alleati."),
                    Create("Vendetta Incandescente", 14, "Al 14° livello, il warlock  può evitare la morte rigenerando metà PF massimi e infliggendo 2d8 + Carisma danni radianti a nemici entro 9m (accecati fino al turno successivo). Utilizzabile 1/riposo lungo."),
                    //Patto della lama del sortilegio
                    Create("Maledizione della Lama del Sortilegio", 1, "Al 1° livello, il warlock può maledire una creatura entro 9m (azione bonus). Per 1 minuto: bonus ai danni pari al bonus competenza, critico su 19-20, e rigenera PF pari a livello + Carisma se uccisa. Utilizzabile 1/riposo."),
                    Create("Combattente del Sortilegio", 1, "Dal 1° livello, il warlock ottiene competenza con armature medie, scudi e armi marziali. Può usare Carisma invece di Forza/Destrezza per un'arma toccata dopo un riposo lungo."),
                    Create("Spettro Maledetto", 6, "Dal 6° livello, quando uccide un umanoide, il warlock può evocare uno spettro che lo serve fino al prossimo riposo lungo. Lo spettro ha PF temporanei pari a metà livello e bonus agli attacchi pari al Carisma."),
                    Create("Armatura dei Sortilegi", 10, "Al 10° livello, il warlock può usare una reazione quando viene colpito dalla creatura maledetta: tira un d6, con 4+ l'attacco fallisce automaticamente."),
                    Create("Maestro dei Sortilegi", 14, "Al 14° livello, quando una creatura maledetta muore, il warlock può trasferire la maledizione a un altro bersaglio entro 9m senza rigenerare PF."),
                    //Patto dell'insondabile
                    Create("Tentacolo delle Profondità", 1, "Al 1° livello, il warlock può evocare un tentacolo spettrale (azione bonus) entro 18m. Attacca (1d8 danni freddo e riduce velocità di 3m). Utilizzabile un numero di volte pari al bonus competenza."),
                    Create("Dono del Mare", 1, "Dal 1° livello, il warlock ottiene velocità di nuoto 12m e capacità di respirare sott'acqua."),
                    Create("Anima Oceanica", 6, "Dal 6° livello, il warlock ottiene resistenza al freddo e può comunicare con creature completamente sommerse."),
                    Create("Spirale Guardiana", 6, "Dal 6° livello, il Tentacolo può ridurre i danni subiti da alleati entro 3m (1d8, aumenta a 2d8 al 10° livello) usando una reazione."),
                    Create("Presa dei Tentacoli", 10, "Al 10° livello, il warlock impara Tentacoli Neri di Evard (lanciabile 1/riposo senza slot) e ottiene PF temporanei pari al suo livello quando lo lancia."),
                    Create("Immersione Insondabile", 14, "Al 14° livello, il warlock può teletrasportarsi con 5 alleati entro 9m fino a 1 miglio di distanza in un corpo d'acqua. Utilizzabile 1/riposo."),
                    //Patto del grande antico
                    Create("Mente Risvegliata", 1, "Dal 1° livello, il warlock può comunicare telepaticamente con qualsiasi creatura entro 9m, indipendentemente dalla lingua conosciuta, purché capace di comprendere almeno un linguaggio."),
                    Create("Interdizione Entropica", 6, "Dal 6° livello, il warlock può usare una reazione per imporre svantaggio a un attacco contro di lui. Se l'attacco fallisce, ottiene vantaggio sul prossimo attacco contro quel bersaglio. Utilizzabile 1/riposo."),
                    Create("Scudo del Pensiero", 10, "Al 10° livello, il warlock è immune alla lettura del pensiero, ha resistenza ai danni psichici e infligge danni psichici riflessi a chi lo colpisce con questo tipo di danno."),
                    Create("Creare Servitore", 14, "Al 14° livello, il warlock può plagiare un umanoide toccandolo (azione): viene Charmato finché non viene curato con Rimuovi Maledizione o si riusa l'abilità. Comunicazione telepatica garantita sullo stesso piano."),
                    //Patto dell'immondo
                    Create("Benedizione dell'Oscuro", 1, "Dal 1° livello, quando riduce una creatura ostile a 0 PF, il warlock del Diavolo ottiene PF temporanei pari al suo modificatore di Carisma + livello (minimo 1)."),
                    Create("Fortuna dell'Oscuro", 6, "Dal 6° livello, il warlock può aggiungere 1d10 a un tiro di abilità o salvataggio dopo aver visto il risultato iniziale. Utilizzabile 1/riposo."),
                    Create("Resilienza Immonda", 10, "Al 10° livello, il warlock può scegliere un tipo di danno dopo ogni riposo, ottenendo resistenza fino alla scelta successiva. Armi magiche o d'argento ignorano la resistenza."),
                    Create("Scagliare all'Inferno", 14, "Al 14° livello, il warlock può scagliare un bersaglio colpito attraverso i piani infernali (azione). Al ritorno, subisce 10d10 danni psichici se non è un demone. Utilizzabile 1/riposo lungo."),
                    //Patto del Genio
                    Create("Recipiente del Genio", 1, "Al 1° livello, il warlock del Genio riceve un recipiente magico che funge da focus. Offre due abilità: Rifugio Portatile (spazio extradimensionale per riposare) e Ira del Genio (danni extra pari al bonus competenza)."),
                    Create("Dono Elementale", 6, "Dal 6° livello, il warlock ottiene resistenza a un tipo di danno in base al patrono e volo 9m per 10 minuti (bonus azione, usi pari al bonus competenza)."),
                    Create("Santuario del Recipiente", 10, "Al 10° livello, il warlock può portare fino a 5 alleati nel recipiente. Chi resta 10+ minuti ottiene i benefici di un riposo breve + bonus competenza ai PF rigenerati."),
                    Create("Desiderio Limitato", 14, "Al 14° livello, il warlock può invocare l'effetto di un incantesimo di 6° livello o inferiore (azione). Utilizzabile 1 volta ogni 1d4 riposi lunghi.")



                };

            await context.ClassFeatures.AddRangeAsync(model);
            await context.SaveChangesAsync();
        }
    }
